const html = $input.item.json.data;
const cheerio = require('cheerio');
const $ = cheerio.load(html);

const categories = ['Category A', 'Category B', 'Category C', 'Category D', 'Category E'];
const coeData = [];
const biddingDate = new Date().toISOString().split('T')[0];

$('table tbody tr').each((index, row) => {
  const cells = $(row).find('td');
  if (cells.length >= 4) {
    const category = $(cells[0]).text().trim();
    const quotaPremium = parseFloat($(cells[1]).text().replace(/[^0-9.]/g, ''));
    const quotaAvailable = parseInt($(cells[2]).text().replace(/[^0-9]/g, ''));
    const bidsReceived = parseInt($(cells[3]).text().replace(/[^0-9]/g, ''));

    if (categories.some(cat => category.includes(cat))) {
      coeData.push({
        biddingDate,
        category,
        quotaPremium,
        quotaAvailable,
        bidsReceived,
        timestamp: new Date().toISOString(),
        biddingRound: new Date().getDate() <= 15 ? 1 : 2
      });
    }
  }
});

if (coeData.length === 0) {
  throw new Error('No COE data extracted - possible website structure change');
}

return coeData.map(item => ({ json: item }));

const items = $input.all();
const categories = ['Category A', 'Category B', 'Category C', 'Category D', 'Category E'];

const processedData = {};

categories.forEach(category => {
  const categoryData = items.filter(item => 
    item.json.category && item.json.category.includes(category)
  ).sort((a, b) => new Date(a.json.biddingDate) - new Date(b.json.biddingDate));

  if (categoryData.length < 4) {
    processedData[category] = { error: 'Insufficient data' };
    return;
  }

  const prices = categoryData.map(item => item.json.quotaPremium);
  const latestPrice = prices[prices.length - 1];

  const ma4 = prices.slice(-4).reduce((a, b) => a + b, 0) / 4;
  const ma12 = prices.length >= 12 ? prices.slice(-12).reduce((a, b) => a + b, 0) / 12 : ma4;
  const ma26 = prices.length >= 26 ? prices.slice(-26).reduce((a, b) => a + b, 0) / 26 : ma12;

  const rateOfChange = prices.length >= 5 ? ((latestPrice - prices[prices.length - 5]) / prices[prices.length - 5]) * 100 : 0;

  const variance = prices.slice(-12).reduce((sum, price) => sum + Math.pow(price - ma12, 2), 0) / Math.min(12, prices.length);
  const volatility = Math.sqrt(variance);

  const now = new Date();
  const month = now.getMonth() + 1;
  const quarter = Math.ceil(month / 3);
  const isCNYPeriod = month === 1 || month === 2;
  const isYearEnd = month === 11 || month === 12;
  const biddingRound = categoryData[categoryData.length - 1].json.biddingRound || 1;

  processedData[category] = {
    latestPrice,
    ma4,
    ma12,
    ma26,
    rateOfChange,
    volatility,
    month,
    quarter,
    isCNYPeriod,
    isYearEnd,
    biddingRound,
    historicalPrices: prices.slice(-26),
    dataPoints: categoryData.length
  };
});

return [{ json: processedData }];

