const recommendations = $input.first().json.recommendations;
const categories = Object.keys(recommendations).filter(k => !recommendations[k].reasoning?.includes('Insufficient'));

const alternatives = {};

categories.forEach(primaryCategory => {
  const primaryRec = recommendations[primaryCategory];
  const alternativeOptions = [];

  categories.forEach(altCategory => {
    if (altCategory === primaryCategory) return;

    const altRec = recommendations[altCategory];
    const priceDiff = altRec.currentPrice - primaryRec.currentPrice;
    const scoreDiff = altRec.score - primaryRec.score;

    if (scoreDiff > 10 || (scoreDiff > 0 && Math.abs(priceDiff) < 10000)) {
      alternativeOptions.push({
        category: altCategory,
        priceDiff: Math.round(priceDiff),
        scoreDiff,
        reasoning: scoreDiff > 15 ? 'Significantly better market conditions' : 'Comparable conditions with different pricing'
      });
    }
  });

  alternatives[primaryCategory] = alternativeOptions.sort((a, b) => b.scoreDiff - a.scoreDiff);
});

return [{ json: { alternatives } }];
