={
  "video_inputs": [
    {
      "character": {
        "type": "avatar",
        "avatar_id": "{{ $('Set Input Vars').item.json.avatar_id }}",
        "avatar_style": "normal",
        "scale": 1,
        "offset": { 
          "x": 0.15,
          "y": 0.15 
        },
        "matting": true
      },
      "voice": {
        "type": "text",
        "input_text": "{{ $('Social Media Agent').item.json.output[0].guion }}",
        "voice_id": "{{ $('Set Input Vars').item.json.voice_id }}",
        "speed": 1.1,
        "pitch": 50,
        "emotion": "Excited"
      },
      "background": {
        "type": "video",
        "url": "{{ $('upload to heygen').item.json.data.url }}",
        "play_style": "loop",
        "fit": "cover"
      }
    }
  ],
  "dimension": { "width": 720, "height": 1280 },
  "aspect_ratio": "9:16",
  "caption": false,
  "title": "n8n-avatar"
}

