const urlsText = $input.item.json.channel_urls;
const channelUrls = urlsText.split('\n').map(url => url.trim()).filter(url => url.length > 0 && url.startsWith('http'));
const processedChannels = channelUrls.map(url => {
  let channelName = '', needsChannelIdFetch = false, rssUrl = '', type = '', fetchUrl = '', videosUrl = url.replace('/videos', '') + '/videos';
  const handleMatch = url.match(/@([^/]+)/);
  if (handleMatch) {
    channelName = handleMatch[1];
    type = 'handle';
    needsChannelIdFetch = true;
    fetchUrl = url.replace('/videos', '');
  }
  const userMatch = url.match(/\/user\/([^/]+)/);
  if (userMatch) {
    channelName = userMatch[1];
    rssUrl = `https://www.youtube.com/feeds/videos.xml?user=${channelName}`;
    type = 'user';
    needsChannelIdFetch = false;
  }
  const channelMatch = url.match(/\/channel\/(UC[^/]+)/);
  if (channelMatch) {
    const channelId = channelMatch[1];
    channelName = channelId;
    rssUrl = `https://www.youtube.com/feeds/videos.xml?channel_id=${channelId}`;
    type = 'channel';
    needsChannelIdFetch = false;
  }
  return {json: {originalUrl: url, channelName, rssUrl, videosUrl, type, needsChannelIdFetch, fetchUrl}};
});
return processedChannels;

const html = $input.first().json.data || '';
const channelData = $input.first().json;
let channelId = null, method = 'none';
let match = html.match(/"channelId":"(UC[^"]+)"/);
if (match && match[1]) { channelId = match[1]; method = 'channelId_regex'; }
if (!channelId) { match = html.match(/"externalId":"(UC[^"]+)"/); if (match && match[1]) { channelId = match[1]; method = 'externalId_regex'; }}
if (!channelId) {
  try {
    const ytInitialMatch = html.match(/var ytInitialData = (\{.+?\});/);
    if (ytInitialMatch && ytInitialMatch[1]) {
      const ytData = JSON.parse(ytInitialMatch[1]);
      channelId = ytData?.metadata?.channelMetadataRenderer?.externalId || ytData?.header?.c4TabbedHeaderRenderer?.channelId;
      if (channelId && channelId.startsWith('UC')) { method = 'ytInitialData'; } else { channelId = null; }
    }
  } catch (e) {}
}
if (channelId) {
  return [{json: {originalUrl: channelData.originalUrl, channelName: channelData.channelName, videosUrl: channelData.videosUrl, type: channelData.type, channelId, rssUrl: `https://www.youtube.com/feeds/videos.xml?channel_id=${channelId}`, method}}];
}
return [{json: {originalUrl: channelData.originalUrl, channelName: channelData.channelName, videosUrl: channelData.videosUrl, type: channelData.type, channelId: null, rssUrl: null, needsScraping: true}}];

const html = $input.first().json.data || '';
const channelData = $input.first().json;
if (!html) return [];
const videos = [];
try {
  const ytInitialMatch = html.match(/var ytInitialData = (\{.+?\});/);
  if (!ytInitialMatch) return [];
  const ytData = JSON.parse(ytInitialMatch[1]);
  const tabs = ytData?.contents?.twoColumnBrowseResultsRenderer?.tabs || [];
  for (const tab of tabs) {
    const contents = tab?.tabRenderer?.content?.sectionListRenderer?.contents || [];
    for (const section of contents) {
      const items = section?.itemSectionRenderer?.contents || [];
      for (const item of items) {
        const gridRenderer = item?.gridRenderer?.items || [];
        for (const gridItem of gridRenderer) {
          const videoRenderer = gridItem?.gridVideoRenderer;
          if (videoRenderer && videoRenderer.videoId) {
            videos.push({json: {channel: channelData.channelName || 'Unknown', title: videoRenderer.title?.runs?.[0]?.text || 'Unknown', url: `https://www.youtube.com/watch?v=${videoRenderer.videoId}`, published: videoRenderer.publishedTimeText?.simpleText || '', source: 'scraped', videoId: videoRenderer.videoId}});
            if (videos.length >= 20) break;
          }
        }
        if (videos.length >= 20) break;
      }
      if (videos.length >= 20) break;
    }
    if (videos.length >= 20) break;
  }
} catch (e) {
  const videoMatches = html.match(/"videoId":"([a-zA-Z0-9_-]{11})"/g) || [];
  for (let i = 0; i < Math.min(videoMatches.length, 20); i++) {
    const match = videoMatches[i].match(/"videoId":"([^"]+)"/);
    if (match && match[1]) {
      videos.push({json: {channel: channelData.channelName || 'Unknown', title: `Video ${i + 1}`, url: `https://www.youtube.com/watch?v=${match[1]}`, published: 'Unknown', source: 'scraped_regex', videoId: match[1]}});
    }
  }
}
return videos;

const items = $input.all();
const now = new Date();
const cutoffDate = new Date(now);
cutoffDate.setHours(cutoffDate.getHours() - 1);
const results = [];
for (const item of items) {
  const data = item.json;
  if (data.feed) {
    const feed = data.feed;
    const channelTitle = feed.title || 'Unknown Channel';
    const entries = feed.entry || [];
    if (Array.isArray(entries)) {
      for (const entry of entries) {
        const publishedStr = entry.published || '';
        const updatedStr = entry.updated || '';
        const publishedDate = publishedStr ? new Date(publishedStr) : null;
        const updatedDate = updatedStr ? new Date(updatedStr) : null;
        const isRecent = (publishedDate && publishedDate >= cutoffDate) || (updatedDate && updatedDate >= cutoffDate);
        if (isRecent) {
          const linkObj = entry.link || {};
          const videoUrl = typeof linkObj === 'object' ? (linkObj.href || '') : '';
          if (videoUrl && !videoUrl.includes('/shorts/')) {
            results.push({json: {channel: channelTitle, title: entry.title || '', url: videoUrl, published: publishedStr, updated: updatedStr, source: 'rss'}});
          }
        }
      }
    }
  } else if (data.source === 'scraped' || data.source === 'scraped_regex') {
    if (data.url && !data.url.includes('/shorts/')) {
      results.push({json: data});
    }
  }
}
return results;

