// Input from Set node
const { topic, regions, search_terms } = $json;

// EDIT: Add or remove platforms as needed
const platforms = ["facebook.com", "instagram.com", "google.com"];

// Calculate a date 1 month ago for freshness filtering
const date = new Date();
date.setMonth(date.getMonth() - 1);
const afterDate = date.toISOString().split("T")[0]; // e.g. "2025-10-09"

// Build output: one record per region √ó platform
const results = [];

regions.forEach(region => {
  platforms.forEach(platform => {
    results.push({
      json: {
        topic,
        region,
        platform,
        search_terms,
        afterDate,
        query: `${topic} ${platform} promotions after:${afterDate}`
      }
    });
  });
});

return results;

// get the raw HTML string from your previous node
const html = $json.results?.[0]?.content || $json.body || "";

// --- BASIC CLEANUP ---

// remove <script>, <style>, <noscript>, and their contents
let cleaned = html.replace(/<script[^>]*>[\s\S]*?<\/script>/gi, "")
                  .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, "")
                  .replace(/<noscript[^>]*>[\s\S]*?<\/noscript>/gi, "");

// remove all HTML tags
cleaned = cleaned.replace(/<\/?[^>]+(>|$)/g, " ");

// decode HTML entities like &amp;, &quot;
cleaned = cleaned.replace(/&nbsp;/gi, " ")
                 .replace(/&amp;/gi, "&")
                 .replace(/&quot;/gi, '"')
                 .replace(/&#39;/gi, "'")
                 .replace(/&lt;/gi, "<")
                 .replace(/&gt;/gi, ">");

// collapse whitespace and line breaks
cleaned = cleaned.replace(/\s+/g, " ").trim();

// optional: cut super-long strings (>10k chars)
if (cleaned.length > 10000) cleaned = cleaned.slice(0, 10000) + " ‚Ä¶ [truncated]";

// return it
return [{ json: { text_clean: cleaned } }];

