let htmlContent = $input.first().json.text;

if (typeof htmlContent === "string") {
  htmlContent = htmlContent
    .replace(/^```(?:html)?\s*/i, "")
    .replace(/\s*```$/i, "");
}

htmlContent = htmlContent.replace(/<\/p>/gi, "</p><br>");

const blogTitle = $('Configuration').first().json['Blog Title'] || "post";
const safeFileName = blogTitle.replace(/[^a-zA-Z0-9-_√§√∂√º√Ñ√ñ√ú√ü ]/g, "_").trim() || "post";
const finalFileName = `${safeFileName}.html`;

const buffer = Buffer.from(htmlContent, "utf-8");

return [
  {
    json: {
      fileName: finalFileName,
      mimeType: "text/html",
    },
    binary: {
      data: {
        data: buffer.toString("base64"),
      },
    },
  },
];

