const gaData = $input.first().json;
const adData = $input.all()[1]?.json || {};
const crmData = $input.all()[2]?.json || {};
const mau = gaData.activeUsers || 0;
const totalAdSpend = adData.totalSpend || 0;
const newCustomers = crmData.newCustomers || 1;
const cac = totalAdSpend / newCustomers;
const totalRevenue = crmData.totalRevenue || 0;
const totalCustomers = crmData.totalCustomers || 1;
const ltv = totalRevenue / totalCustomers;
const sessions = gaData.sessions || 0;
const conversions = gaData.conversions || 0;
const conversionRate = sessions > 0 ? (conversions / sessions * 100).toFixed(2) : 0;
return [{
  json: {
    reportDate: new Date().toISOString(),
    periodStart: $now.minus({ days: $('Workflow Configuration').first().json.reportPeriodDays }).toFormat('yyyy-MM-dd'),
    periodEnd: $now.minus({ days: 1 }).toFormat('yyyy-MM-dd'),
    mau: mau,
    cac: parseFloat(cac.toFixed(2)),
    ltv: parseFloat(ltv.toFixed(2)),
    ltvCacRatio: parseFloat((ltv / cac).toFixed(2)),
    sessions: sessions,
    conversions: conversions,
    conversionRate: parseFloat(conversionRate),
    totalAdSpend: totalAdSpend,
    newCustomers: newCustomers,
    totalRevenue: totalRevenue
  }
}];

