// Iterate through all incoming items and create one summary per mediaInfo
const items = $input.all();
const results = [];

for (const item of items) {
  const info = item.json.mediaInfo || {};

  const description = info.description?.trim() || 'No description';
  const filename = `photo-${info.imageId || info.id || Date.now()}.txt`;

  results.push({
    json: {
      filename,
      text: `Title: ${info.title || 'Untitled'}\nDescription: ${description}\n---\n`
    }
  });
}

return results;

// Convert all text summaries to binary files for Google Drive upload
const items = $input.all();
const results = [];

for (const item of items) {
  const { text, filename } = item.json;
  const buffer = Buffer.from(text, 'utf8');

  results.push({
    json: { filename },
    binary: {
      data: {
        data: buffer.toString('base64'),
        mimeType: 'text/plain',
        fileName: filename
      }
    }
  });
}

return results;

// üîÅ 1Ô∏è‚É£ Global store initialisieren
const store = $getWorkflowStaticData('global');
if (!store.uploadedPhotoIds) store.uploadedPhotoIds = [];

// üîÅ 2Ô∏è‚É£ Alle eingehenden Items holen
const items = $input.all();
const newItems = [];

// üîÅ 3Ô∏è‚É£ Schleife: nur neue Bilder behalten
for (const item of items) {
  const id = item.json.id;
  if (!id) continue;

  if (!store.uploadedPhotoIds.includes(id)) {
    store.uploadedPhotoIds.push(id);
    console.log(`‚úÖ Neues Foto erkannt: ${id}`);
    newItems.push(item);
  } else {
    console.log(`‚ö†Ô∏è Foto ${id} wurde bereits verarbeitet, √ºberspringe`);
  }
}

// üîÅ 4Ô∏è‚É£ Alles Neue als Batch zur√ºckgeben
return newItems;

// üßπ Clear stored photo IDs after the session ends
const store = $getWorkflowStaticData('global');

if (store.uploadedPhotoIds && store.uploadedPhotoIds.length > 0) {
  console.log(`üßπ Removing ${store.uploadedPhotoIds.length} stored photo IDs from the upload cache`);
  store.uploadedPhotoIds = [];
} else {
  console.log('‚úÖ No stored photo ID cache ‚Äì nothing to clean up');
}

return $input.all();

// üß† Safe conversion of session summary text to binary for Google Drive upload
const store = $getWorkflowStaticData('global');
if (!store.sessionSummaryFiles || typeof store.sessionSummaryFiles !== 'object') {
  store.sessionSummaryFiles = {};
}

// üîπ Get session info
const sessionItems = $items('Set Session Vars', 0, 0);
const sessionId = sessionItems[0]?.json?.sessionId || 'unknown-session';

// üîπ Skip if we already uploaded a summary for this session
if (store.sessionSummaryFiles[sessionId]) {
  console.log(`‚úÖ Session ${sessionId} summary already uploaded (file ${store.sessionSummaryFiles[sessionId]}). Skipping re-upload.`);
  return [];
}

// üîπ Extract summary text from API response (robustly handle all cases)
let raw = $json.data ?? $json.body ?? $json.text ?? $json.mediaSummary ?? '';
if (typeof raw !== 'string') {
  try {
    raw = JSON.stringify(raw, null, 2);
  } catch (e) {
    raw = String(raw);
  }
}

const summaryText = raw.trim() || '‚ö†Ô∏è Empty summary received from API';
const filename = `session-summary-${sessionId}.txt`;

let buffer;
try {
  buffer = Buffer.from(summaryText, 'utf8');
} catch (e) {
  buffer = Buffer.from('‚ö†Ô∏è Error creating buffer from summary text', 'utf8');
}

return [{
  json: { filename, sessionId },
  binary: {
    data: {
      data: buffer.toString('base64'),
      mimeType: 'text/plain',
      fileName: filename,
      fileExtension: 'txt'
    }
  }
}];

// Track uploaded summary file IDs per session so we only upload once.
const store = $getWorkflowStaticData('global');
if (!store.sessionSummaryFiles || typeof store.sessionSummaryFiles !== 'object') {
  store.sessionSummaryFiles = {};
}

const sessionItems = $items('Set Session Vars', 0, 0);
const sessionId = sessionItems[0]?.json?.sessionId || 'unknown-session';
const uploadedId = $json?.id;

if (uploadedId) {
  store.sessionSummaryFiles[sessionId] = uploadedId;
  console.log(`üìÑ Stored session summary file ${uploadedId} for ${sessionId}`);
} else {
  console.log('‚ö†Ô∏è No uploaded summary file ID found ‚Äì nothing stored.');
}

return $input.all();
