  (() => {
    // Base: "now" (no upstream node needed)
    const now = new Date();

    // Work in UTC to avoid timezone edge cases
    const today = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate()));
    const day = today.getUTCDay(); // 0=Sun ... 6=Sat

    const shiftUTC = (base, days) => {
      const d = new Date(base.getTime());
      d.setUTCDate(d.getUTCDate() + days);
      return d;
    };

    let start, end;
    if (day === 0) {
      // If today is Sunday (normally the schedule) â†’ upcoming week
      start = today;
      end = shiftUTC(start, 6);
    } else {
      // Otherwise â†’ current week from previous Sunday
      start = shiftUTC(today, -day);
      end = shiftUTC(start, 6);
    }

    // Format like "Nov 9 â€“ Nov 15, 2025" without locale reliance
    const mon = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    const fmt = (d) => `${mon[d.getUTCMonth()]} ${d.getUTCDate()}`;
    const year = start.getUTCFullYear();
    return `${fmt(start)} â€“ ${fmt(end)}, ${year}`;
  })()
}}
