// Parse GAME_TILE widgets and extract game info
const data = items[0].json;
const out = [];

function walk(n) {
  if (Array.isArray(n)) return n.forEach(walk);
  if (n && typeof n === 'object') {
    if (n.type === 'GAME_TILE') {
      const target = n.actions?.[0]?.target || '';
      const parts = target.replace(/^\/|\/$/g,'').split('/');
      const slug = parts[1] || null;
      const asin = parts[2] || null;

      let pd = {};
      try { pd = JSON.parse(n.presentationData || '{}'); } catch(e){}

      out.push({
        asin,
        slug,
        title: pd.title || null,
        releaseYear: pd.releaseYear || null,
        publishers: pd.publishersText || null,
        genres: Array.isArray(pd.genreTags) ? pd.genreTags.join(', ') : null,
        productUrl: pd.productUrl || null,
        imagePortrait: pd.imagePortrait || null,
        imageLandscape: pd.imageLandscape || null,
        ageRating: pd.ageRating?.categoryText || null,
        target
      });
    }
    Object.values(n).forEach(walk);
  }
}
walk(data);

return out.map(x => ({ json: x }));

// Leggo tutte le righe dal nodo "Get row(s) in sheet"
const existingRows = $items('Get row(s) in sheet');

// Se il foglio √® vuoto: tutti nuovi
if (!existingRows || existingRows.length === 0) {
  return items.map(item => {
    item.json.isNew = true;
    return item;
  });
}

// Costruisco le chiavi gi√† presenti nel foglio:
// prima asin, se vuoto uso il titolo (Title nel foglio, title negli item)
const existingKeys = new Set(
  existingRows
    .map(r => {
      const asin = (r.json.asin || '').trim().toUpperCase();
      const title =
        (r.json.Title || r.json.title || '').trim().toUpperCase(); // üëà QUI la differenza
      return asin || title;
    })
    .filter(k => k !== '')
);

// Aggiungo isNew agli item in ingresso
return items.map(item => {
  const asin = (item.json.asin || '').trim().toUpperCase();
  const title =
    (item.json.Title || item.json.title || '').trim().toUpperCase();

  const key = asin || title;

  // se non ho n√© asin n√© titolo, lo considero nuovo
  item.json.isNew = key === '' ? true : !existingKeys.has(key);

  return item;
});

