// Extract DNS records (robust)
try {
 const dns = $input.first().json || {};
 const setNode = $node["Set Target Domain1"];
 const rawTarget = setNode?.json?.target_domain || '';
 const domain = rawTarget.replace(/^https?:\/\//i, '').replace(/\/.*$/, '');

 const answers = Array.isArray(dns.Answer) ? dns.Answer : [];
 const fallback = Array.isArray(dns.Authority) ? dns.Authority : (Array.isArray(dns.Additional) ? dns.Additional : []);
 const raw = answers.length ? answers : fallback

 const seen = new Set();
 const resolveChain = async (name) => {
 if (seen.has(name)) return [];
 seen.add(name);
 const res = await $http.get(`https://dns.google/resolve?name=${name}&type=ANY`);
 const ips = [];
 for (const a of (res.Answer || [])) {
 if (a.type === 5) { // CNAME
 ips.push(...await resolveChain(a.data));
 } else if ([1,28].includes(a.type)) {
 ips.push(a.data);
 }
 }
 return ips.length ? ips : [null];
 };

 const items = [];
 for (const a of raw) {
 const recordTypeCode = typeof a.type === 'string' ? parseInt(a.type, 10) : a.type;
 let humanType = '';
 if (recordTypeCode === 1) humanType = 'A';
 else if (recordTypeCode === 5) humanType = 'CNAME';
 else if (recordTypeCode === 28) humanType = 'AAAA';
 else humanType = String(recordTypeCode || 'UNKNOWN');

 items.push({
 json: {
 target_domain: domain || rawTarget,
 record_name: a.name || domain || rawTarget,
 record_type: humanType,
 record_data: a.data || '',
 ttl: a.TTL || a.ttl || null,
 raw: a
 }
 });
 }

 if (items.length === 0) {
 items.push({
 json: {
 target_domain: domain || rawTarget,
 record_type: 'NONE',
 record_data: 'No DNS answers found',
 ttl: null
 }
 });
 }

 return items;
} catch (err) {
 return [{
 json: {
 target_domain: $node["Set Target Domain1"]?.json?.target_domain || '',
 error: String(err?.message || err)
 }
 }];
}

// Defensive Parse Shodan Data - supports multiple input items and safe fallbacks
const outputs = [];

try {
 // NEW ‚Äî rely on the input to this node
 const rawDomain = $json?.target_domain || '';
 const normalizedDomain = String(rawDomain).replace(/^https?:\/\/|\/.*$/i, '');

 // If there are no input items, return a helpful diagnostic
 if (!$input || $input.length === 0) {
 return [{
 json: {
 timestamp: new Date().toISOString(),
 domain: normalizedDomain || 'N/A',
 ip_address: 'N/A',
 port: '',
 service: '',
 technologies: '',
 http_info: '',
 vulnerabilities: 'No Shodan input items received',
 organization: 'N/A',
 location: 'N/A',
 last_update: 'N/A',
 _diagnostic: 'No input items to Parse Shodan Data node'
 }
 }];
 }

 // Process each input item (one Shodan API response per IP expected)
 for (let idx = 0; idx < $input.length; idx++) {
 const item = $input[idx];
 const shodan = (item && item.json) ? item.json : {};
 // Resolve domain and ip from either the Set node or the input item
 const ipFromInput = item.json?.ip || item.json?.ip_address || item.json?.ip_str || item.json?.host || '';
 const domain = normalizedDomain || item.json?.target_domain || '';
 const ip = ipFromInput || '';

 // If Shodan explicitly returned an error, produce a single diagnostic row
 if (shodan.error) {
 outputs.push({
 json: {
 timestamp: new Date().toISOString(),
 domain,
 ip_address: ip || 'N/A',
 port: '',
 service: '',
 technologies: '',
 http_info: '',
 vulnerabilities: `Shodan error: ${shodan.error}`,
 organization: shodan.org || 'N/A',
 location: `${shodan.city || ''}${shodan.country_name ? ', ' + shodan.country_name : ''}`.trim() || 'N/A',
 last_update: shodan.last_update || 'N/A',
 _diagnostic: 'shodan.error present',
 source_links: [
 ...(shodanHostUrl ? [{ label: 'Shodan Host Page', url: shodanHostUrl }] : []),
 ...(shodanApiUrl ? [{ label: 'Shodan API (no key)', url: shodanApiUrl }] : [])
 ],
 _input_index: idx,
 _raw_shodan: shodan
 }
 });
 // go to next input
 continue;
 }

 // Prefer 'data' array (banners); otherwise try ports list
 const banners = Array.isArray(shodan.data) ? shodan.data : [];
 const portsList = Array.isArray(shodan.ports) ? shodan.ports : [];

 if (banners.length === 0 && portsList.length === 0) {
 // No detailed info found ‚Äî return a single fallback row for this input
 outputs.push({
 json: {
 timestamp: new Date().toISOString(),
 domain,
 ip_address: ip || 'N/A',
 port: '',
 service: '',
 technologies: '',
 http_info: '',
 vulnerabilities: Object.keys(shodan).length === 0 ? 'No data' : 'No banners/ports available',
 organization: shodan.org || 'N/A',
 location: `${shodan.city || ''}${shodan.country_name ? ', ' + shodan.country_name : ''}`.trim() || 'N/A',
 last_update: shodan.last_update || 'N/A',
 source_links: [
 ...(shodanHostUrl ? [{ label: 'Shodan Host Page', url: shodanHostUrl }] : []),
 ...(shodanApiUrl ? [{ label: 'Shodan API (no key)', url: shodanApiUrl }] : [])
 ],
 _diagnostic: 'no banners and no ports',
 _input_index: idx,
 _raw_shodan: shodan
 }
 });
 continue;
 }

 // If banners exist, create one output item per banner
 if (banners.length > 0) {
 for (const b of banners) {
 outputs.push({
 json: {
 timestamp: new Date().toISOString(),
 domain,
 ip_address: ip || (b.ip_str || b.ip || ''),
 port: b.port ? String(b.port) : (b.port || ''),
 service: ((b.product || b.transport) || '') + (b.version ? ' ' + b.version : ''),
 technologies: [b.product, b.version, b.devicetype, shodan.os].filter(Boolean).join(' | ') || 'N/A',
 http_info: b.http ? (((b.http.title || '') + (b.http.server ? ' | ' + b.http.server : '')).trim()) : '',
 vulnerabilities: b.vulns ? Object.keys(b.vulns).join(', ') : 'None detected',
 organization: shodan.org || 'N/A',
 location: `${shodan.city || ''}${shodan.country_name ? ', ' + shodan.country_name : ''}`.trim() || 'N/A',
 last_update: shodan.last_update || 'N/A',
 raw: b,
 _input_index: idx
 }
 });
 }
 continue;
 }

 // Fallback: if no banners but ports list exists, produce one item per port
 if (portsList.length > 0) {
 for (const p of portsList) {
 outputs.push({
 json: {
 timestamp: new Date().toISOString(),
 domain,
 ip_address: ip || '',
 port: String(p),
 service: '',
 technologies: '',
 http_info: '',
 vulnerabilities: 'N/A',
 organization: shodan.org || 'N/A',
 location: `${shodan.city || ''}${shodan.country_name ? ', ' + shodan.country_name : ''}`.trim() || 'N/A',
 last_update: shodan.last_update || 'N/A',
 _input_index: idx,
 _raw_shodan: shodan
 }
 });
 }
 continue;
 }
 } // end per-input loop

 // Always return something (zero-length arrays cause some nodes to hang); if outputs empty, return diagnostic
 if (outputs.length === 0) {
 return [{
 json: {
 timestamp: new Date().toISOString(),
 domain: normalizedDomain || 'N/A',
 ip_address: 'N/A',
 vulnerabilities: 'No output produced by parser',
 _diagnostic: 'parser returned no outputs'
 }
 }];
 }

 return outputs;
} catch (err) {
 // Never mutate the Error object ‚Äî return a safe error item instead
 return [{
 json: {
 timestamp: new Date().toISOString(),
 domain: (typeof $node?.["Set Target Domain"]?.json?.target_domain === 'string') ? $node["Set Target Domain"].json.target_domain : 'N/A',
 ip_address: ($json?.ip_address) ? $json.ip_address : ($json?.ip || 'N/A'),
 error: String(err?.message || err),
 _diagnostic: 'exception in Parse Shodan Data',
 _raw_error: err && (err.stack || err.message || String(err))
 }
 }];
}
