// Edge Preprocessing & Validation
// Validate and preprocess incoming sensor data

const item = $input.item.json;

// Initialize result object
let result = {
  valid: false,
  errors: [],
  warnings: [],
  dataQualityScore: 0
};

try {
  // Parse JSON payload if it's a string
  let sensorData;
  if (typeof item === 'string') {
    try {
      sensorData = JSON.parse(item);
    } catch (e) {
      result.errors.push('Invalid JSON payload');
      return result;
    }
  } else {
    sensorData = item;
  }

  // Validate required fields
  const requiredFields = ['sensor_id', 'timestamp', 'value', 'unit'];
  const missingFields = [];

  for (const field of requiredFields) {
    if (!sensorData.hasOwnProperty(field) || sensorData[field] === null || sensorData[field] === undefined) {
      missingFields.push(field);
    }
  }

  if (missingFields.length > 0) {
    result.errors.push(`Missing required fields: ${missingFields.join(', ')}`);
    return result;
  }

  // Validate sensor_id
  if (typeof sensorData.sensor_id !== 'string' || sensorData.sensor_id.trim() === '') {
    result.errors.push('Invalid sensor_id: must be a non-empty string');
  }

  // Validate timestamp
  const timestamp = new Date(sensorData.timestamp);
  if (isNaN(timestamp.getTime())) {
    result.errors.push('Invalid timestamp format');
  } else {
    // Check if timestamp is in the future or too old (more than 7 days)
    const now = new Date();
    const sevenDaysAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);

    if (timestamp > now) {
      result.warnings.push('Timestamp is in the future');
    } else if (timestamp < sevenDaysAgo) {
      result.warnings.push('Timestamp is older than 7 days');
    }
  }

  // Validate value
  const value = parseFloat(sensorData.value);
  if (isNaN(value)) {
    result.errors.push('Invalid value: must be a number');
  } else {
    // Check for extreme values (potential sensor malfunction)
    if (value < -1000 || value > 10000) {
      result.warnings.push('Value is outside typical range');
    }
  }

  // Validate unit
  if (typeof sensorData.unit !== 'string' || sensorData.unit.trim() === '') {
    result.errors.push('Invalid unit: must be a non-empty string');
  }

  // If there are errors, mark as invalid
  if (result.errors.length > 0) {
    result.valid = false;
    result.dataQualityScore = 0;
    return result;
  }

  // Calculate data quality score (0-100)
  let qualityScore = 100;

  // Deduct points for warnings
  qualityScore -= result.warnings.length * 10;

  // Deduct points for missing optional fields
  const optionalFields = ['location', 'device_type', 'metadata'];
  const missingOptional = optionalFields.filter(field => !sensorData.hasOwnProperty(field));
  qualityScore -= missingOptional.length * 5;

  // Ensure score is between 0 and 100
  qualityScore = Math.max(0, Math.min(100, qualityScore));

  result.dataQualityScore = qualityScore;
  result.valid = true;

  // Return cleaned data
  return {
    ...result,
    cleanedData: {
      sensor_id: sensorData.sensor_id.trim(),
      timestamp: timestamp.toISOString(),
      value: value,
      unit: sensorData.unit.trim(),
      location: sensorData.location || null,
      device_type: sensorData.device_type || null,
      metadata: sensorData.metadata || {},
      processed_at: new Date().toISOString()
    }
  };

} catch (error) {
  result.errors.push(`Processing error: ${error.message}`);
  result.valid = false;
  return result;
}

