// Statistical Anomaly Detection using Z-score method

// Get all input items
const items = $input.all();

// Get threshold from workflow config (default to 3 if not set)
const threshold = $('Workflow Configuration').item.json.anomaly_threshold || 3;

// Extract all sensor readings for statistical analysis
const readings = items.map(item => item.json.value || item.json.reading || 0);

// Calculate mean
const mean = readings.reduce((sum, val) => sum + val, 0) / readings.length;

// Calculate standard deviation
const variance = readings.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / readings.length;
const stdDev = Math.sqrt(variance);

// Process each item and calculate Z-score
const enrichedItems = items.map(item => {
  const value = item.json.value || item.json.reading || 0;

  // Calculate Z-score
  const zScore = stdDev === 0 ? 0 : (value - mean) / stdDev;

  // Flag as anomaly if Z-score exceeds threshold
  const isAnomaly = Math.abs(zScore) > threshold;

  return {
    json: {
      ...item.json,
      anomaly_score: Math.abs(zScore),
      is_anomaly: isAnomaly,
      statistical_analysis: {
        z_score: zScore,
        mean: mean,
        std_dev: stdDev,
        threshold: threshold,
        deviation_from_mean: value - mean
      }
    }
  };
});

return enrichedItems;
