// Helper function to extract text with Regex
function extractWithRegex(text, regex) {
  try {
    const match = text.match(regex);
    // [0] is full match, [1] is capture group first
    return match && match[1] ? match[1].trim() : '';
  } catch (e) {
    return '';
  }
}

// Helper function to extract text between two strings
function extractBetween(text, startStr, endStr) {
  try {
    const startIndex = text.indexOf(startStr);
    if (startIndex === -1) return '';

    const textAfterStart = text.substring(startIndex + startStr.length);
    const endIndex = textAfterStart.indexOf(endStr);
    if (endIndex === -1) return '';

    return textAfterStart.substring(0, endIndex).trim();
  } catch (e) {
    return '';
  }
}


const allLeads = [];

// 1. Loop through the incoming n8n items
for (const item of items) {
  try {
    // item.json is the OBJECT: { "results": [...] }
    const dataObject = item.json; 

    // 2. Check if the "results" array exists on this object
    if (dataObject && dataObject.results && Array.isArray(dataObject.results)) {

      // 3. Loop through the "results" array
      for (const [index, result] of dataObject.results.entries()) {

        const html = result.content;
        const searchQuery = result.query || 'unknown_query';

        if (!html || typeof html !== 'string') {
          continue;
        }

        // *** FIX: Split by the parent container 'jscontroller="AtSb"' ***
        // This keeps the lat/lng div grouped with its business info
        const chunks = html.split('<div jscontroller="AtSb"');
        chunks.shift(); // Remove the part before the first listing

        if (chunks.length === 0) {
          continue; 
        }

        // Loop through each business HTML chunk
        for (const chunk of chunks) {

          const businessName = extractBetween(chunk, 'class="dbg0pd" aria-level="3" role="heading"><span class="OSrXXb">', '</span></div>');

          if (!businessName) {
            continue; // Not a valid listing
          }

          const detailsDiv = extractBetween(chunk, '</span></div><div>', '</div><div>');
          let address = detailsDiv;
          let phone = '';
          if (detailsDiv.includes('·')) {
            const parts = detailsDiv.split('·');
            address = parts[0] ? parts[0].trim() : '';
            phone = parts[1] ? parts[1].trim() : '';
          }

          const openingHours = extractBetween(chunk, detailsDiv + '</div><div>', '</div>');
          const description = extractBetween(chunk, '<span class="uDyWh OSrXXb btbrud">"', '"</span>');

          // *** FIX: Made Regex more specific to get the "Rute" (Route) link ***
          let googleMapsUrl = extractWithRegex(chunk, /href="(\/maps\/dir\/[^"]*)" style="cursor:pointer"/);
          if (googleMapsUrl) {
             googleMapsUrl = 'https://www.google.com' + googleMapsUrl;
          }
          const website = extractWithRegex(chunk, /href="(http[^"]*)" data-ved="[^"]*">\s*<div class="wLAgVc"[^>]*>.*?<span class="BSaJxc">Situs<\/span>/);
          const orderUrl = extractWithRegex(chunk, /data-url="(https:\/\/www\.google\.com\/viewer\/chooseprovider[^"]*)"/);

          // *** FIX: Lat/Lng are now inside the 'chunk' because we split correctly ***
          const lat = extractWithRegex(chunk, /data-lat="(-?[0-9.]*)"/);
          const lng = extractWithRegex(chunk, /data-lng="(-?[0-9.]*)"/);
          const cid = extractWithRegex(chunk, /data-cid="([0-9]*)"/);

          const lead = {
            id: cid || `lead_${searchQuery}_${index}`,
            businessName: businessName,
            category: 'Restoran',
            address: address,
            phone: phone,
            website: website,
            rating: 0,
            reviewCount: 0,
            latitude: lat ? parseFloat(lat) : null,
            longitude: lng ? parseFloat(lng) : null,
            openingHours: openingHours.replace(/<[^>]*>/g, ''),
            priceLevel: '',
            description: description,
            googleMapsUrl: googleMapsUrl,
            orderUrl: orderUrl,
            scrapedAt: new Date().toISOString(),
            searchQuery: searchQuery
          };

          allLeads.push({ json: lead });
        }
      }
    } else {
      allLeads.push({ json: { error: 'Skipped item: Input did not contain a .results array.', input: item.json } });
    }
  } catch (error) {
    allLeads.push({ json: { error: `Gagal parse item: ${error.message}`, input: item.json } });
  }
}

// 6. Return all extracted leads
if (allLeads.length === 0) {
  throw new Error('No valid leads were extracted from the input data.');
}

return allLeads;

// Optimized error handler - clean & informative
const errorData = $input.first().json;

function getNestedValue(obj, path, defaultValue = 'Unknown') {
  return path.split('.').reduce((curr, prop) => 
    curr?.[prop], obj) || defaultValue;
}

const workflowName = getNestedValue(errorData, 'workflow.name', 'Unknown Workflow');
const nodeName = getNestedValue(errorData, 'execution.error.node.name', 'Unknown Node');
const errorMessage = getNestedValue(errorData, 'execution.error.message', 'No error message');
const timestamp = getNestedValue(errorData, 'execution.startedAt', new Date().toISOString());
const executionId = getNestedValue(errorData, 'execution.id', 'Unknown');

// Tangkap error details
const errorObj = errorData.execution?.error || {};
const errorDescription = errorObj.description || '';
const errorHttpCode = errorObj.httpCode || '';

// Stack trace - ambil HANYA baris pertama yang paling penting
const errorStack = errorObj.stack || '';
const mainError = errorStack.split('\n')[0] || '';

// Format detail yang clean
let errorDetails = [];
if (errorDescription && errorDescription !== errorMessage) {
  errorDetails.push(errorDescription);
}
if (errorHttpCode) {
  errorDetails.push(`HTTP ${errorHttpCode}`);
}

const detailText = errorDetails.length > 0 
  ? errorDetails.join(' | ') 
  : 'No additional details';

// Main error dari stack (tanpa "NodeApiError: " prefix)
const cleanMainError = mainError.replace(/^(NodeApiError|Error):\s*/, '').trim();

// Build message - COMPACT VERSION
const message = `🚨 <b>WORKFLOW ERROR</b>\n\n` +
  `📋 <b>${workflowName}</b>\n` +
  `⚙️ Node: <code>${nodeName}</code>\n\n` +
  `❌ <b>${errorMessage}</b>\n` +
  `💡 ${detailText}\n\n` +
  `🕐 ${new Date(timestamp).toLocaleString('id-ID', { 
    day: '2-digit', 
    month: 'short', 
    hour: '2-digit', 
    minute: '2-digit' 
  })}\n` +
  `🔗 Exec: <code>${executionId}</code>`;

return {
  json: {
    message: message
  }
};
