// Clean and normalize text data
const item = $input.item.json;

// Function to remove HTML tags
function removeHtmlTags(text) {
  if (!text) return '';
  return text.replace(/<[^>]*>/g, '');
}

// Function to normalize unicode characters
function normalizeUnicode(text) {
  if (!text) return '';
  return text.normalize('NFKD');
}

// Function to remove special characters (keep alphanumeric, spaces, and basic punctuation)
function removeSpecialCharacters(text) {
  if (!text) return '';
  return text.replace(/[^a-zA-Z0-9\s.,!?;:'-]/g, '');
}

// Function to clean and normalize text
function cleanText(text) {
  if (!text) return '';

  // Remove HTML tags
  let cleaned = removeHtmlTags(text);

  // Trim whitespace
  cleaned = cleaned.trim();

  // Normalize unicode characters
  cleaned = normalizeUnicode(cleaned);

  // Remove special characters
  cleaned = removeSpecialCharacters(cleaned);

  // Remove extra whitespace (multiple spaces to single space)
  cleaned = cleaned.replace(/\s+/g, ' ');

  // Convert to lowercase for consistency
  cleaned = cleaned.toLowerCase();

  return cleaned;
}

// Apply cleaning to text fields
const cleanedItem = {
  ...item,
  text_cleaned: cleanText(item.text || item.content || item.message || item.feedback || ''),
  text_original: item.text || item.content || item.message || item.feedback || ''
};

// If there's a title or subject field, clean that too
if (item.title) {
  cleanedItem.title_cleaned = cleanText(item.title);
  cleanedItem.title_original = item.title;
}

if (item.subject) {
  cleanedItem.subject_cleaned = cleanText(item.subject);
  cleanedItem.subject_original = item.subject;
}

return cleanedItem;

// Calculate sentiment trends from aggregated feedback data
// Compute rolling averages, detect spikes, and calculate sentiment distribution

const items = $input.all();

// Initialize results object
const results = {
  sentimentDistribution: {},
  rollingAverages: [],
  spikes: [],
  trendsBySource: {},
  trendsByTimePeriod: {}
};

// Helper function to calculate sentiment score
function getSentimentScore(sentiment) {
  const scores = {
    'positive': 1,
    'neutral': 0,
    'negative': -1
  };
  return scores[sentiment?.toLowerCase()] || 0;
}

// Process each item
const dataPoints = [];
for (const item of items) {
  const data = item.json;
  const sentimentScore = getSentimentScore(data.sentiment);

  dataPoints.push({
    timestamp: data.timestamp || data.created_at || new Date().toISOString(),
    sentiment: data.sentiment,
    score: sentimentScore,
    source: data.source || 'unknown',
    entity: data.entity || 'general'
  });
}

// Sort by timestamp
dataPoints.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

// Calculate sentiment distribution
const distribution = { positive: 0, neutral: 0, negative: 0 };
for (const point of dataPoints) {
  const sentiment = point.sentiment?.toLowerCase();
  if (distribution.hasOwnProperty(sentiment)) {
    distribution[sentiment]++;
  }
}
results.sentimentDistribution = distribution;

// Calculate rolling averages (7-day window)
const windowSize = 7;
for (let i = 0; i < dataPoints.length; i++) {
  const windowStart = Math.max(0, i - windowSize + 1);
  const window = dataPoints.slice(windowStart, i + 1);
  const avgScore = window.reduce((sum, p) => sum + p.score, 0) / window.length;

  results.rollingAverages.push({
    timestamp: dataPoints[i].timestamp,
    averageScore: avgScore,
    windowSize: window.length
  });
}

// Detect spikes (significant deviations from rolling average)
const spikeThreshold = 0.3;
for (let i = windowSize; i < dataPoints.length; i++) {
  const currentScore = dataPoints[i].score;
  const avgScore = results.rollingAverages[i].averageScore;
  const deviation = Math.abs(currentScore - avgScore);

  if (deviation > spikeThreshold) {
    results.spikes.push({
      timestamp: dataPoints[i].timestamp,
      sentiment: dataPoints[i].sentiment,
      score: currentScore,
      averageScore: avgScore,
      deviation: deviation,
      source: dataPoints[i].source
    });
  }
}

// Calculate trends by source
const sourceGroups = {};
for (const point of dataPoints) {
  if (!sourceGroups[point.source]) {
    sourceGroups[point.source] = [];
  }
  sourceGroups[point.source].push(point.score);
}

for (const [source, scores] of Object.entries(sourceGroups)) {
  const avgScore = scores.reduce((sum, s) => sum + s, 0) / scores.length;
  results.trendsBySource[source] = {
    averageScore: avgScore,
    count: scores.length,
    positiveRate: scores.filter(s => s > 0).length / scores.length,
    negativeRate: scores.filter(s => s < 0).length / scores.length
  };
}

// Calculate trends by time period (daily)
const periodGroups = {};
for (const point of dataPoints) {
  const date = new Date(point.timestamp).toISOString().split('T')[0];
  if (!periodGroups[date]) {
    periodGroups[date] = [];
  }
  periodGroups[date].push(point.score);
}

for (const [date, scores] of Object.entries(periodGroups)) {
  const avgScore = scores.reduce((sum, s) => sum + s, 0) / scores.length;
  results.trendsByTimePeriod[date] = {
    averageScore: avgScore,
    count: scores.length,
    positiveCount: scores.filter(s => s > 0).length,
    neutralCount: scores.filter(s => s === 0).length,
    negativeCount: scores.filter(s => s < 0).length
  };
}

// Return results
return [{ json: results }];

