// Generate Comprehensive Insights Report
const items = $input.all();

if (!items || items.length === 0) {
  return [{ json: { error: 'No data available for insights generation' } }];
}

// Initialize data structures for analysis
const sentimentCounts = { positive: 0, neutral: 0, negative: 0 };
const entitySentiments = {};
const emotionCounts = {};
const timeSeriesData = {};

// Process all items
items.forEach(item => {
  const data = item.json;

  // Count sentiments
  if (data.sentiment) {
    const sentiment = data.sentiment.toLowerCase();
    if (sentimentCounts[sentiment] !== undefined) {
      sentimentCounts[sentiment]++;
    }
  }

  // Track entity sentiments
  if (data.entity && data.sentiment) {
    if (!entitySentiments[data.entity]) {
      entitySentiments[data.entity] = { positive: 0, neutral: 0, negative: 0, total: 0 };
    }
    const sentiment = data.sentiment.toLowerCase();
    if (entitySentiments[data.entity][sentiment] !== undefined) {
      entitySentiments[data.entity][sentiment]++;
    }
    entitySentiments[data.entity].total++;
  }

  // Track emotions
  if (data.emotion) {
    emotionCounts[data.emotion] = (emotionCounts[data.emotion] || 0) + 1;
  }

  // Track time series (by date)
  if (data.timestamp || data.date) {
    const date = new Date(data.timestamp || data.date).toISOString().split('T')[0];
    if (!timeSeriesData[date]) {
      timeSeriesData[date] = { positive: 0, neutral: 0, negative: 0, total: 0 };
    }
    const sentiment = (data.sentiment || '').toLowerCase();
    if (timeSeriesData[date][sentiment] !== undefined) {
      timeSeriesData[date][sentiment]++;
    }
    timeSeriesData[date].total++;
  }
});

// Calculate top positive entities
const topPositiveEntities = Object.entries(entitySentiments)
  .map(([entity, sentiments]) => ({
    entity,
    positiveCount: sentiments.positive,
    positivePercentage: ((sentiments.positive / sentiments.total) * 100).toFixed(2),
    totalMentions: sentiments.total
  }))
  .sort((a, b) => b.positiveCount - a.positiveCount)
  .slice(0, 10);

// Calculate top negative entities
const topNegativeEntities = Object.entries(entitySentiments)
  .map(([entity, sentiments]) => ({
    entity,
    negativeCount: sentiments.negative,
    negativePercentage: ((sentiments.negative / sentiments.total) * 100).toFixed(2),
    totalMentions: sentiments.total
  }))
  .sort((a, b) => b.negativeCount - a.negativeCount)
  .slice(0, 10);

// Calculate overall sentiment distribution
const totalFeedback = items.length;
const sentimentDistribution = {
  positive: {
    count: sentimentCounts.positive,
    percentage: ((sentimentCounts.positive / totalFeedback) * 100).toFixed(2)
  },
  neutral: {
    count: sentimentCounts.neutral,
    percentage: ((sentimentCounts.neutral / totalFeedback) * 100).toFixed(2)
  },
  negative: {
    count: sentimentCounts.negative,
    percentage: ((sentimentCounts.negative / totalFeedback) * 100).toFixed(2)
  }
};

// Top emotions
const topEmotions = Object.entries(emotionCounts)
  .map(([emotion, count]) => ({
    emotion,
    count,
    percentage: ((count / totalFeedback) * 100).toFixed(2)
  }))
  .sort((a, b) => b.count - a.count)
  .slice(0, 5);

// Sentiment trends over time
const sentimentTrends = Object.entries(timeSeriesData)
  .map(([date, sentiments]) => ({
    date,
    positive: sentiments.positive,
    neutral: sentiments.neutral,
    negative: sentiments.negative,
    total: sentiments.total,
    sentimentScore: ((sentiments.positive - sentiments.negative) / sentiments.total * 100).toFixed(2)
  }))
  .sort((a, b) => new Date(a.date) - new Date(b.date));

// Generate actionable recommendations
const recommendations = [];

// Recommendation based on negative sentiment
if (sentimentDistribution.negative.percentage > 30) {
  recommendations.push({
    priority: 'HIGH',
    category: 'Sentiment Alert',
    recommendation: `Negative sentiment is at ${sentimentDistribution.negative.percentage}%. Immediate action required to address customer concerns.`,
    action: 'Review top negative entities and create action plan to address issues'
  });
}

// Recommendations for top negative entities
if (topNegativeEntities.length > 0 && topNegativeEntities[0].negativePercentage > 50) {
  recommendations.push({
    priority: 'HIGH',
    category: 'Entity Focus',
    recommendation: `Entity "${topNegativeEntities[0].entity}" has ${topNegativeEntities[0].negativePercentage}% negative sentiment.`,
    action: `Investigate issues related to ${topNegativeEntities[0].entity} and implement corrective measures`
  });
}

// Positive reinforcement recommendation
if (topPositiveEntities.length > 0 && topPositiveEntities[0].positivePercentage > 70) {
  recommendations.push({
    priority: 'MEDIUM',
    category: 'Success Story',
    recommendation: `Entity "${topPositiveEntities[0].entity}" has ${topPositiveEntities[0].positivePercentage}% positive sentiment.`,
    action: `Leverage success of ${topPositiveEntities[0].entity} in marketing and replicate best practices`
  });
}

// Emotion-based recommendation
if (topEmotions.length > 0 && ['anger', 'frustration', 'disappointment'].includes(topEmotions[0].emotion.toLowerCase())) {
  recommendations.push({
    priority: 'HIGH',
    category: 'Emotional Response',
    recommendation: `Top emotion detected is "${topEmotions[0].emotion}" (${topEmotions[0].percentage}%).`,
    action: 'Prioritize customer support and proactive outreach to address emotional concerns'
  });
}

// Trend-based recommendation
if (sentimentTrends.length >= 2) {
  const recentTrend = sentimentTrends.slice(-3);
  const avgRecentScore = recentTrend.reduce((sum, t) => sum + parseFloat(t.sentimentScore), 0) / recentTrend.length;

  if (avgRecentScore < -10) {
    recommendations.push({
      priority: 'HIGH',
      category: 'Trend Alert',
      recommendation: `Recent sentiment trend is declining (avg score: ${avgRecentScore.toFixed(2)}).`,
      action: 'Analyze recent changes in product/service and address emerging issues quickly'
    });
  } else if (avgRecentScore > 20) {
    recommendations.push({
      priority: 'LOW',
      category: 'Positive Trend',
      recommendation: `Recent sentiment trend is improving (avg score: ${avgRecentScore.toFixed(2)}).`,
      action: 'Continue current strategies and document successful practices'
    });
  }
}

// General recommendation if no specific issues
if (recommendations.length === 0) {
  recommendations.push({
    priority: 'LOW',
    category: 'Maintenance',
    recommendation: 'Sentiment levels are stable. Continue monitoring for changes.',
    action: 'Maintain current customer engagement strategies and monitor trends'
  });
}

// Compile comprehensive report
const insightsReport = {
  reportGeneratedAt: new Date().toISOString(),
  summary: {
    totalFeedbackAnalyzed: totalFeedback,
    reportingPeriod: sentimentTrends.length > 0 ? {
      startDate: sentimentTrends[0].date,
      endDate: sentimentTrends[sentimentTrends.length - 1].date
    } : null,
    overallSentimentScore: ((sentimentCounts.positive - sentimentCounts.negative) / totalFeedback * 100).toFixed(2)
  },
  sentimentDistribution,
  topPositiveEntities,
  topNegativeEntities,
  topEmotions,
  sentimentTrends,
  recommendations,
  keyInsights: [
    `${sentimentDistribution.positive.percentage}% of feedback is positive`,
    `${sentimentDistribution.negative.percentage}% of feedback is negative`,
    topPositiveEntities.length > 0 ? `Best performing entity: ${topPositiveEntities[0].entity}` : 'No entity data available',
    topNegativeEntities.length > 0 ? `Needs attention: ${topNegativeEntities[0].entity}` : 'No negative entities identified',
    topEmotions.length > 0 ? `Most common emotion: ${topEmotions[0].emotion}` : 'No emotion data available'
  ]
};

return [{ json: insightsReport }];
