// CRITICAL: Get original webhook data (before email verification)
// The VerifiEmail node only outputs verification result, not the original fields
const webhookData = $('Webhook').first().json.body;

// Get verification result from current input
const verificationData = $input.first().json;

// Generate unique pass ID
const passId = "GYM-" + Date.now();

// Get current timestamp
const issuedAt = new Date().toISOString();

// Format dates nicely for display
const formatDate = (dateStr) => {
  if (!dateStr) return 'Not provided';
  const date = new Date(dateStr);
  if (isNaN(date.getTime())) return 'Invalid Date';
  return date.toLocaleDateString('en-US', { 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric' 
  });
};

// Return all data from webhook + new generated fields
return [{
  json: {
    name: webhookData.name,
    email: webhookData.email,
    photo_url: webhookData.photo_url,
    start_date: webhookData.start_date,
    valid_till: webhookData.valid_till,
    pass_id: passId,
    issued_at: issuedAt,
    start_date_formatted: formatDate(webhookData.start_date),
    valid_till_formatted: formatDate(webhookData.valid_till),
    email_verified: verificationData.valid || false
  }
}];

