const executions = $input.all().map(item => item.json);

// Basic metrics 
const totalExecutions = executions.length;
const failedExecutions = executions.filter(exec => exec.status === 'error');
const successfulExecutions = executions.filter(exec => exec.status === 'success');
const failedCount = failedExecutions.length;
const successCount = successfulExecutions.length;
const failureRate = totalExecutions > 0 ? ((failedCount / totalExecutions) * 100).toFixed(2) : 0;

// All the unique workflows that had failures
const workflowsWithFailures = [...new Set(failedExecutions.map(exec => exec.workflowId))];
const workflowsWithFailuresCount = workflowsWithFailures.length;

// Group failed executions by workflow
const failedByWorkflow = {};
failedExecutions.forEach(exec => {
  if (!failedByWorkflow[exec.workflowId]) {
    failedByWorkflow[exec.workflowId] = {
      workflowId: exec.workflowId,
      failures: [],
      failureCount: 0
    };
  }
  failedByWorkflow[exec.workflowId].failures.push({
    id: exec.id,
    startedAt: exec.startedAt,
    stoppedAt: exec.stoppedAt,
    mode: exec.mode
  });
  failedByWorkflow[exec.workflowId].failureCount++;
});

// Calculate execution modes distribution
const executionModes = executions.reduce((acc, exec) => {
  acc[exec.mode] = (acc[exec.mode] || 0) + 1;
  return acc;
}, {});

// Get all unique workflows
const allWorkflows = [...new Set(executions.map(exec => exec.workflowId))];
const totalWorkflowsExecuted = allWorkflows.length;

// Metrics per workflow
const workflowMetrics = {};
allWorkflows.forEach(workflowId => {
  const workflowExecs = executions.filter(exec => exec.workflowId === workflowId);
  const workflowFailed = workflowExecs.filter(exec => exec.status === 'error').length;
  const workflowSuccess = workflowExecs.filter(exec => exec.status === 'success').length;

  workflowMetrics[workflowId] = {
    workflowId: workflowId,
    totalExecutions: workflowExecs.length,
    successfulExecutions: workflowSuccess,
    failedExecutions: workflowFailed,
    successRate: workflowExecs.length > 0 ? 
      ((workflowSuccess / workflowExecs.length) * 100).toFixed(2) + '%' : '0%',
    failureRate: workflowExecs.length > 0 ? 
      ((workflowFailed / workflowExecs.length) * 100).toFixed(2) + '%' : '0%',
    lastExecution: workflowExecs[0]?.startedAt || null,
    executionModes: workflowExecs.reduce((acc, exec) => {
      acc[exec.mode] = (acc[exec.mode] || 0) + 1;
      return acc;
    }, {})
  };
});

// Sort workflows by failure rate (highest rate first)
const workflowsByFailureRate = Object.values(workflowMetrics)
  .sort((a, b) => parseFloat(b.failureRate) - parseFloat(a.failureRate))
  .slice(0, 10); // Top 10 problematic workflows

// Calculate time-based metrics
const now = new Date();
const executionsByHour = {};

executions.forEach(exec => {
  const startTime = new Date(exec.startedAt);
  const hourKey = startTime.toISOString().slice(0, 13) + ':00'; // Round to hour

  if (!executionsByHour[hourKey]) {
    executionsByHour[hourKey] = {
      total: 0,
      success: 0,
      error: 0
    };
  }

  executionsByHour[hourKey].total++;
  if (exec.status === 'success') {
    executionsByHour[hourKey].success++;
  } else if (exec.status === 'error') {
    executionsByHour[hourKey].error++;
  }
});

// Calculate average execution time for finished executions
const executionTimes = executions
  .filter(exec => exec.finished && exec.startedAt && exec.stoppedAt)
  .map(exec => {
    const start = new Date(exec.startedAt);
    const stop = new Date(exec.stoppedAt);
    return (stop - start) / 1000; // ms to seconds
  });

const avgExecutionTime = executionTimes.length > 0 
  ? (executionTimes.reduce((a, b) => a + b, 0) / executionTimes.length).toFixed(2)
  : 0;

const maxExecutionTime = executionTimes.length > 0 
  ? Math.max(...executionTimes).toFixed(2)
  : 0;

const minExecutionTime = executionTimes.length > 0 
  ? Math.min(...executionTimes).toFixed(2)
  : 0;

// Find most recent failures
const recentFailures = failedExecutions
  .sort((a, b) => new Date(b.startedAt) - new Date(a.startedAt))
  .slice(0, 5)
  .map(exec => ({
    id: exec.id,
    workflowId: exec.workflowId,
    startedAt: exec.startedAt,
    mode: exec.mode
  }));

// Return all indicators
const kpis = {
  summary: {
    totalExecutions: totalExecutions,
    successfulExecutions: successCount,
    failedExecutions: failedCount,
    failureRate: failureRate + '%',
    successRate: (100 - parseFloat(failureRate)).toFixed(2) + '%',
    totalWorkflowsExecuted: totalWorkflowsExecuted,
    workflowsWithFailures: workflowsWithFailuresCount
  },

  executionModes: executionModes,

  timing: {
    averageExecutionTime: avgExecutionTime + ' seconds',
    maxExecutionTime: maxExecutionTime + ' seconds',
    minExecutionTime: minExecutionTime + ' seconds',
    timeRange: {
      from: executions[executions.length - 1]?.startedAt || null,
      to: executions[0]?.startedAt || null
    }
  },

  failureAnalysis: {
    workflowsImpactedByFailures: workflowsWithFailures,
    failedExecutionsByWorkflow: failedByWorkflow,
    recentFailures: recentFailures
  },

  workflowPerformance: {
    allWorkflowMetrics: workflowMetrics,
    topProblematicWorkflows: workflowsByFailureRate
  },

  timeSeriesData: executionsByHour,

  alerts: {
    highFailureRate: parseFloat(failureRate) > 10,
    failureRateThreshold: parseFloat(failureRate) > 10 ? 
      `‚ö†Ô∏è High failure rate detected: ${failureRate}%` : 
      `‚úÖ Failure rate within acceptable range: ${failureRate}%`,
    workflowsNeedingAttention: Object.values(workflowMetrics)
      .filter(wf => parseFloat(wf.failureRate) > 20)
      .map(wf => ({
        workflowId: wf.workflowId,
        failureRate: wf.failureRate,
        failedCount: wf.failedExecutions
      }))
  }
};

// Return the KPIs object
return kpis;
