// === ‰øÆÊ≠£Áâà Prepare Reply ===

// ÂêÑitem„Åî„Å®„Å´„É™„Éó„É©„Ç§„Éá„Éº„Çø„ÇíÁîüÊàê
return items.map(item => {
  const tweet = item.json.text;
  const tweetId = item.json.id;
  const authorId = item.json.author_id;
  const includes = item.json.includes || {};
  const users = includes.users || [];

  // „É¶„Éº„Ç∂„ÉºÂêç„ÇíÂèñÂæó
  let username = 'unknown';
  const user = users.find(u => u.id === authorId);
  if (user && user.username) {
    username = user.username;
  }

  // Âõ∫ÂÆö„É°„ÉÉ„Çª„Éº„Ç∏ÔºàÂøÖË¶Å„Å™„Çâ„Åì„Åì„ÇíÂÄãÂà•ÂØæÂøú„Å´Â§âÊõ¥ÂèØÔºâ
  const replyMessage = `ÂºäÁ§æ„ÅÆÊñ∞„Çµ„Éº„Éì„Çπ„Çí„ÅîË¶ß„Åè„Å†„Åï„ÅÑÔºÅ https://x.com/linepi_pi/status/1988478476761133063`;

  return {
    json: {
      ...item.json,
      replyText: replyMessage,
      replyToTweetId: tweetId,
      extractedUsername: username,
      originalTweet: tweet,
    },
  };
});

// === ‰øÆÊ≠£Áâà ===
// Twitter API„ÅÆ„É¨„Çπ„Éù„É≥„Çπ„Çí„Åô„Åπ„Å¶„ÅÆitems„Åã„ÇâÁµêÂêà
const allResponses = items.map(item => item.json);

// „Éá„Éº„Çø„ÇíÁµ±Âêà
const mergedData = {
  data: [],
  includes: { users: [] },
};

for (const res of allResponses) {
  if (res.data) mergedData.data.push(...res.data);
  if (res.includes?.users) {
    const existingUserIds = new Set(mergedData.includes.users.map(u => u.id));
    for (const user of res.includes.users) {
      if (!existingUserIds.has(user.id)) {
        mergedData.includes.users.push(user);
      }
    }
  }
}

// „Ç®„É©„Éº„ÉÅ„Çß„ÉÉ„ÇØ
if (mergedData.error || mergedData.errors) {
  const errorMessage =
    mergedData.error?.message ||
    mergedData.errors?.[0]?.message ||
    JSON.stringify(mergedData.error || mergedData.errors);

  let errorType = 'unknown_error';
  if (errorMessage.includes('Too Many Requests') || errorMessage.includes('Rate limit')) {
    errorType = 'rate_limit';
  } else if (errorMessage.includes('Unauthorized') || errorMessage.includes('authentication')) {
    errorType = 'auth_error';
  } else if (errorMessage.includes('Bad request')) {
    errorType = 'bad_request';
  }

  return [
    {
      json: {
        status: 'error',
        errorType,
        errorMessage,
      },
    },
  ];
}

const tweets = mergedData.data || [];
const users = mergedData.includes.users || [];

if (tweets.length === 0) {
  return [
    {
      json: {
        status: 'no_tweets',
        message: 'Áõ£Ë¶ñÂØæË±°„É¶„Éº„Ç∂„Éº„Åã„Çâ„ÅÆ„ÉÑ„Ç§„Éº„Éà„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„Åß„Åó„Åü',
      },
    },
  ];
}

// author_id„Åî„Å®„Å´ÊúÄÊñ∞„ÉÑ„Ç§„Éº„Éà„ÅÆ„ÅøÂèñÂæó
const latestTweetsByAuthor = {};
for (const tweet of tweets) {
  const authorId = tweet.author_id;
  if (
    !latestTweetsByAuthor[authorId] ||
    new Date(tweet.created_at) > new Date(latestTweetsByAuthor[authorId].created_at)
  ) {
    latestTweetsByAuthor[authorId] = tweet;
  }
}

// ÁµêÊûúÈÖçÂàó
const latestTweets = Object.values(latestTweetsByAuthor);

// includes„ÇíÂÜç‰ªò‰∏é„Åó„Å¶Ëøî„Åô
return latestTweets.map(tweet => ({
  json: {
    ...tweet,
    includes: { users },
    status: 'success',
    totalAccountsFound: latestTweets.length,
    totalTweetsScanned: tweets.length,
  },
}));
