const data = items[0].json.orders;

if (!Array.isArray(data)) {
  throw new Error("Orders not found or not an array");
}

const customersMap = {};

data.forEach((order) => {
  const customer = order.customer || {};
  const customerId = customer.id || null;
  if (!customerId) return;

  if (!customersMap[customerId]) {
    const phone = customer.phone || (customer.default_address && customer.default_address.phone) || "N/A";
    const name = [customer.first_name, customer.last_name].filter(Boolean).join(" ") || "N/A";

    customersMap[customerId] = {
      customerId: customerId,
      name: name,
      email: customer.email || "N/A",
      phone: phone,
      totalSpent: 0,
      products: {},
      lastOrderLink: order.order_status_url || "N/A",
    };
  } else {
    customersMap[customerId].lastOrderLink = order.order_status_url || customersMap[customerId].lastOrderLink;
  }

  customersMap[customerId].totalSpent += parseFloat(order.current_total_price || "0");

  if (Array.isArray(order.line_items)) {
    order.line_items.forEach((item) => {
      if (item.name && item.id) {
        if (!customersMap[customerId].products[item.id]) {
          customersMap[customerId].products[item.id] = {
            productName: item.name,
            quantity: 0,
          };
        }
        customersMap[customerId].products[item.id].quantity += item.quantity || 1;
      }
    });
  }
});

const result = Object.values(customersMap).map((customer) => ({
  json: {
    customerId: customer.customerId,
    name: customer.name,
    email: customer.email,
    phone: customer.phone,
    re_order_link: customer.lastOrderLink,
    totalSpent: customer.totalSpent.toFixed(2),
    productsBought: Object.entries(customer.products).map(
      ([productId, productData]) => ({
        productId: productId,
        productName: productData.productName,
        quantity: productData.quantity,
      }),
    ),
  },
}));

return result;

const data = items[0].json.customer;

if (!data || typeof data !== "object") {
  throw new Error("Customer data not found or invalid format");
}

const customers = Array.isArray(data) ? data : [data];

return customers
  .filter((customer) => {
    const totalSpent = parseFloat(customer.total_spent || '0.00');
    return totalSpent > 0;
  })
  .map((customer) => {
    const address = customer.default_address || {};

    return {
      json: {
        customerId: customer.id || null,
        customerName: `${customer.first_name || ''} ${customer.last_name || ''}`.trim(),
        email: customer.email || 'N/A',
        phone: customer.phone || address.phone || 'N/A',
        totalSpent: customer.total_spent || '0.00',
        ordersCount: customer.orders_count || 0,
        address: address.address1 || 'N/A',
        city: address.city || 'N/A',
        country: address.country || 'N/A',
        createdAt: customer.created_at || null,
        updatedAt: customer.updated_at || null,
        state: customer.state || 'N/A'
      }
    };
  });
