return items.map(item => {
  let raw = item.json.output;

  // If the output is empty, skip or return an empty object
  if (!raw || raw.trim() === '') {
    return { json: { error: "Empty output", rawOutput: raw || null } };
  }

  // Remove markdown backticks like ```json or ``` and trim
  raw = raw.replace(/```json|```/g, '').trim();

  try {
    // Parse the cleaned JSON
    const parsed = JSON.parse(raw);
    return { json: parsed };
  } catch (err) {
    // If JSON is invalid, return an error for debugging
    return {
      json: {
        error: "Invalid JSON",
        message: err.message,
        rawOutput: raw
      }
    };
  }
});

