return items.map(item => {
  let raw = item.json.output;

  try {
    if (typeof raw === 'string') {
      raw = raw.trim();
      if (raw.startsWith('{') === false) {
        raw = raw.replace(/^"+|"+$/g, '').replace(/\\"/g, '"');
      }
      raw = JSON.parse(raw);
    }
  } catch (err) {
    return {
      json: { error: 'Invalid JSON input', message: err.message, raw: item.json.output }
    };
  }

  return {
    json: raw
  };
});

