// n8n Code Node - Facebook Posts Data Formatter with Comments
// Access the input items correctly in n8n

const items = $input.all();
const formattedPosts = [];

// Loop through each input item
for (const item of items) {
  // Access the data array - handle both wrapped and unwrapped formats
  let postsData = [];

  if (Array.isArray(item.json)) {
    // If json is an array, get the first element's data
    postsData = item.json[0]?.data || [];
  } else if (item.json.data) {
    // If json has a data property directly
    postsData = item.json.data;
  } else if (Array.isArray(item.json)) {
    // If json itself is the data array
    postsData = item.json;
  }

  // Process each post
  for (const post of postsData) {
    // Format comments data
    const commentsData = (post.comments?.data || []).map(comment => ({
      commentId: comment.id || '',
      commentMessage: comment.message || '',
      commentCreatedTime: comment.created_time || '',
      commentLikeCount: comment.like_count || 0,
      commentCount: comment.comment_count || 0,
      commentor: {
        id: comment.from?.id || '',
        name: comment.from?.name || ''
      },
      commentReactions: {
        count: comment.reactions?.summary?.total_count || 0,
        viewerReaction: comment.reactions?.summary?.viewer_reaction || 'NONE'
      }
    }));

    const formattedPost = {
      // Basic Post Information
      postId: post.id || '',
      message: post.message || '',
      createdTime: post.created_time || '',
      permalinkUrl: post.permalink_url || '',

      // Page Information
      pageInfo: {
        pageId: post.from?.id || '',
        pageName: post.from?.name || '',
        pageCategory: post.from?.category || ''
      },

      // Media Information
      media: {
        hasMedia: (post.attachments?.data?.length || 0) > 0,
        fullPictureUrl: post.full_picture || '',
        attachments: (post.attachments?.data || []).map(att => ({
          mediaType: att.media_type || '',
          url: att.url || '',
          description: att.description || '',
          imageUrl: att.media?.image?.src || '',
          imageWidth: att.media?.image?.width || 0,
          imageHeight: att.media?.image?.height || 0
        }))
      },

      // Engagement Metrics
      engagement: {
        likes: {
          count: post.likes?.summary?.total_count || 0,
          canLike: post.likes?.summary?.can_like || false,
          hasLiked: post.likes?.summary?.has_liked || false
        },
        comments: {
          count: post.comments?.summary?.total_count || 0,
          canComment: post.comments?.summary?.can_comment || false
        },
        reactions: {
          count: post.reactions?.summary?.total_count || 0,
          viewerReaction: post.reactions?.summary?.viewer_reaction || 'NONE'
        }
      },

      // Comments Array - DETAILED COMMENT DATA
      comments: commentsData,

      // Metadata
      metadata: {
        hasComments: (post.comments?.summary?.total_count || 0) > 0,
        hasLikes: (post.likes?.summary?.total_count || 0) > 0,
        hasReactions: (post.reactions?.summary?.total_count || 0) > 0,
        totalEngagement: (post.likes?.summary?.total_count || 0) + 
                        (post.comments?.summary?.total_count || 0) + 
                        (post.reactions?.summary?.total_count || 0),
        totalComments: commentsData.length
      }
    };

    formattedPosts.push(formattedPost);
  }
}

// Return the formatted posts in n8n format
return formattedPosts.map(post => ({ json: post }));

// n8n Code Node - Format Sentiment Analysis Data for Email

const data = $input.all();

// Helper function to get sentiment color
function getSentimentColor(label) {
  const colors = {
    'Positive': '#10b981',
    'Negative': '#ef4444',
    'Neutral': '#6b7280'
  };
  return colors[label] || '#6b7280';
}

// Helper function to get emoji for sentiment
function getSentimentEmoji(label) {
  const emojis = {
    'Positive': 'üòä',
    'Negative': 'üòû',
    'Neutral': 'üòê'
  };
  return emojis[label] || 'üòê';
}

// Process each item
const emailData = data.map(item => {
  const output = item.json.output;

  // Build HTML for comments
  let commentsHtml = '';
  if (output.comments && output.comments.length > 0) {
    commentsHtml = output.comments.map(comment => `
      <div style="background-color: #f9fafb; border-left: 4px solid ${getSentimentColor(comment.label)}; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <p style="margin: 0 0 10px 0; color: #374151; font-size: 14px;">${comment.commentMessage}</p>
        <div style="display: flex; align-items: center; gap: 10px;">
          <span style="background-color: ${getSentimentColor(comment.label)}; color: white; padding: 4px 12px; border-radius: 12px; font-size: 12px; font-weight: 600;">
            ${getSentimentEmoji(comment.label)} ${comment.label}
          </span>
          <span style="color: #6b7280; font-size: 12px;">Score: ${(comment.score * 100).toFixed(0)}%</span>
        </div>
        <p style="margin: 10px 0 0 0; color: #6b7280; font-size: 12px; font-style: italic;">${comment.explanation}</p>
      </div>
    `).join('');
  } else {
    commentsHtml = '<p style="color: #6b7280; font-style: italic;">No comments yet.</p>';
  }

  // Build complete HTML email
  const htmlBody = `
    <!DOCTYPE html>
    <html>
    <head>
      <meta charset="utf-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
    </head>
    <body style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif; line-height: 1.6; color: #1f2937; background-color: #f3f4f6; margin: 0; padding: 20px;">
      <div style="max-width: 650px; margin: 0 auto; background-color: white; border-radius: 10px; box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1); overflow: hidden;">

        <div style="background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); padding: 30px; text-align: center;">
          <h1 style="color: white; margin: 0; font-size: 24px;">üìä Social Media Sentiment Report</h1>
        </div>

        <div style="padding: 30px;">

          <div style="margin-bottom: 30px;">
            <h2 style="color: #1f2937; font-size: 20px; margin: 0 0 15px 0; border-bottom: 2px solid #e5e7eb; padding-bottom: 10px;">
              üìù Post Analysis
            </h2>

            <div style="background-color: #f9fafb; padding: 20px; border-radius: 8px; border: 1px solid #e5e7eb;">
              <p style="margin: 0 0 15px 0; font-size: 16px; color: #374151; font-weight: 500;">${output.postMessage}</p>

              <div style="display: flex; align-items: center; gap: 15px; flex-wrap: wrap;">
                <span style="background-color: ${getSentimentColor(output.postSentiment.label)}; color: white; padding: 6px 16px; border-radius: 20px; font-size: 14px; font-weight: 600;">
                  ${getSentimentEmoji(output.postSentiment.label)} ${output.postSentiment.label}
                </span>
                <span style="color: #6b7280; font-size: 14px;">
                  <strong>Confidence:</strong> ${(output.postSentiment.score * 100).toFixed(0)}%
                </span>
              </div>

              <p style="margin: 15px 0 0 0; color: #6b7280; font-size: 13px; font-style: italic; border-top: 1px solid #e5e7eb; padding-top: 15px;">
                ${output.postSentiment.explanation}
              </p>
            </div>

            <p style="margin: 10px 0 0 0; color: #9ca3af; font-size: 12px;">
              <strong>Post ID:</strong> ${output.postId}
            </p>
          </div>

          <div style="margin-bottom: 30px;">
            <h2 style="color: #1f2937; font-size: 20px; margin: 0 0 15px 0; border-bottom: 2px solid #e5e7eb; padding-bottom: 10px;">
              üí¨ Comments (${output.comments ? output.comments.length : 0})
            </h2>
            ${commentsHtml}
          </div>

          <div style="background: linear-gradient(135deg, #667eea15 0%, #764ba215 100%); padding: 20px; border-radius: 8px; border: 2px solid ${getSentimentColor(output.overallCommentSentiment.label)};">
            <h3 style="color: #1f2937; font-size: 18px; margin: 0 0 15px 0;">
              üìà Overall Comment Sentiment
            </h3>

            <div style="display: flex; align-items: center; gap: 15px; margin-bottom: 15px; flex-wrap: wrap;">
              <span style="background-color: ${getSentimentColor(output.overallCommentSentiment.label)}; color: white; padding: 8px 20px; border-radius: 20px; font-size: 16px; font-weight: 600;">
                ${getSentimentEmoji(output.overallCommentSentiment.label)} ${output.overallCommentSentiment.label}
              </span>
              <span style="color: #374151; font-size: 16px; font-weight: 500;">
                Score: ${(output.overallCommentSentiment.score * 100).toFixed(0)}%
              </span>
            </div>

            <p style="margin: 0; color: #4b5563; font-size: 14px;">
              ${output.overallCommentSentiment.explanation}
            </p>
          </div>

        </div>

        <div style="background-color: #f9fafb; padding: 20px; text-align: center; border-top: 1px solid #e5e7eb;">
          <p style="margin: 0; color: #6b7280; font-size: 12px;">
            Generated by n8n Sentiment Analysis Workflow | ${new Date().toLocaleString()}
          </p>
        </div>

      </div>
    </body>
    </html>
  `;

  // Generate subject line
  const subject = 'Sentiment Report: ' + output.postSentiment.label + ' Post with ' + (output.comments ? output.comments.length : 0) + ' Comments';

  return {
    json: {
      subject: subject,
      htmlBody: htmlBody,
      postId: output.postId,
      postSentiment: output.postSentiment.label,
      commentCount: output.comments ? output.comments.length : 0,
      overallSentiment: output.overallCommentSentiment.label
    }
  };
});

return emailData;

// n8n Code Node - Format Sentiment Data for Google Sheets
// This flattens the nested sentiment data into sheet-friendly rows

const items = $input.all();
const sheetRows = [];

for (const item of items) {
  const data = item.json.output || item.json;

  // Create main post row
  const postRow = {
    // Post Information
    postId: data.postId || '',
    postMessage: data.postMessage || '',

    // Post Sentiment
    postSentimentScore: data.postSentiment?.score || 0,
    postSentimentLabel: data.postSentiment?.label || '',
    postSentimentExplanation: data.postSentiment?.explanation || '',

    // Overall Comment Sentiment
    overallCommentScore: data.overallCommentSentiment?.score || 0,
    overallCommentLabel: data.overallCommentSentiment?.label || '',
    overallCommentExplanation: data.overallCommentSentiment?.explanation || '',

    // Comment Counts
    totalComments: data.comments?.length || 0,
    positiveComments: data.comments?.filter(c => c.label === 'Positive').length || 0,
    negativeComments: data.comments?.filter(c => c.label === 'Negative').length || 0,
    neutralComments: data.comments?.filter(c => c.label === 'Neutral').length || 0,

    // Timestamp
    analyzedAt: new Date().toISOString(),

    // Alert Flag
    needsAttention: data.overallCommentSentiment?.label === 'Negative' || 
                    data.overallCommentSentiment?.score < -0.3,

    // Comments Details (JSON string for reference)
    commentsJson: JSON.stringify(data.comments || [])
  };

  sheetRows.push(postRow);
}

// Return formatted data for Google Sheets
return sheetRows.map(row => ({ json: row }));
