// Input: Combined JSON from X and Facebook (like in your example)
const allData = $input.all();

// Helper: normalize date safely
function normalizeDate(dateString) {
  if (!dateString) return null;
  try {
    return new Date(dateString).toISOString();
  } catch {
    return dateString;
  }
}

const unified = [];

// Iterate over each input item
for (const item of allData) {
  const dataArray = item.json.data || [];

  // --- Case 1: X (Twitter) Mentions ---
  if (dataArray[0]?.text && dataArray[0]?.public_metrics) {
    for (const tweet of dataArray) {
      unified.push({
        platform: "Twitter",
        post_id: tweet.id,
        author_id: tweet.author_id,
        message: tweet.text,
        created_at: normalizeDate(tweet.created_at),
        likes: tweet.public_metrics.like_count,
        replies: tweet.public_metrics.reply_count,
        impressions: tweet.public_metrics.impression_count,
        retweets: tweet.public_metrics.retweet_count,
        link: `https://x.com/i/web/status/${tweet.id}`,
        sentiment_status: "", // can be filled later by AI
      });
    }
  }

  // --- Case 2: Facebook Posts + Comments ---
  if (dataArray[0]?.message && dataArray[0]?.comments) {
    for (const post of dataArray) {
      const comments = post.comments?.data || [];
      for (const c of comments) {
        unified.push({
          platform: "Facebook",
          post_id: post.id,
          comment_id: c.id,
          commenter: c.from?.name || "Unknown",
          message: c.message,
          created_at: normalizeDate(c.created_time),
          likes: c.like_count || 0,
          reactions: c.reactions?.summary?.total_count || 0,
          post_link: post.permalink_url,
          sentiment_status: "", // for AI analysis later
        });
      }
    }
  }
}

// --- Output ---
return [
  {
    json: {
      total_items: unified.length,
      unified_comments: unified,
      summary: {
        twitter_mentions: unified.filter(i => i.platform === "Twitter").length,
        facebook_comments: unified.filter(i => i.platform === "Facebook").length,
      },
    },
  },
];

