// Get raw AI output (string)
const raw = $json.output;

// Remove markdown wrappers (```json ... ```)
const cleaned = raw.replace(/```json|```/g, '').trim();

let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (error) {
  parsed = { error: "Failed to parse JSON", raw: cleaned };
}

// Extract the main sections for clarity
const overall = parsed.overall_summary || {};
const details = parsed.detailed_analysis || [];

// Return structured data
return [
  {
    json: {
      overall_summary: overall,
      detailed_analysis: details,
      total_posts: details.length
    }
  }
];
