// Loop through AI results and clean up JSON output
return items.map(item => {
  let raw = item.json.output || '';

  // Remove code block markers like ```json or ```
  raw = raw.replace(/```json|```/g, '').trim();

  // Parse JSON safely
  let parsed;
  try {
    parsed = JSON.parse(raw);
  } catch (e) {
    parsed = { subject: "No Subject", body_html: "Error parsing AI output." };
  }

  return { json: parsed };
});

// Loop through all email threads where reply = true
return items.map(item => {
  const data = item.json;

  // Extract the reply message (second email in thread)
  const reply = data.messages?.[1];
  const original = data.messages?.[0];

  // Clean the reply text (remove quoted text from Gmail)
  let clientMessage = reply?.snippet || "";
  clientMessage = clientMessage.split("On Wed")[0].trim(); // basic cleanup example

  // Prepare clean structured output
  return {
    json: {
      Email: reply?.From?.match(/<(.+?)>/)?.[1] || reply?.From || "",
      ContactPerson: reply?.From?.split("<")[0].trim() || "",
      ThreadID: reply?.threadId || data.id || "",
      Subject: reply?.Subject || original?.Subject || "",
      ClientMessage: clientMessage,
      BookingStatus: "BOOKED",
      LastUpdated: new Date().toISOString()
    }
  };
});

