// Process each item (one per lead)
return items.map(item => {
  // Get raw AI text output
  let text = item.json.output || "";

  // üßπ Step 1: Clean the text (remove ```json, ```, and unwanted quotes)
  text = text
    .replace(/```json/g, "")   // remove ```json
    .replace(/```/g, "")       // remove ```
    .replace(/\n/g, " ")       // flatten newlines
    .trim();

  // üß† Step 2: Try parsing it as JSON
  let parsed;
  try {
    parsed = JSON.parse(text);
  } catch (err) {
    parsed = { error: "Invalid JSON", raw: text };
  }

  // ‚úÖ Step 3: Return clean structured data
  return {
    json: {
      summary: parsed.summary || "",
      one_pager: parsed.one_pager || parsed.onePager || "",
      proposal: parsed.proposal || "",
    }
  };
});

// n8n automatically provides Buffer, no need to import it

return items.map((item, index) => {
  const data = item.json;

  // Build text report for each lead
  let report = "";
  report += `Lead Report #${index + 1}\n`;
  report += "===================================\n\n";

  // === Summary ===
  report += `üìã SALES SUMMARY\n${data.summary}\n\n`;

  // === One Pager ===
  const onePager = data.one_pager || {};
  report += `üìÑ ONE PAGER\nHeadline: ${onePager.headline || "N/A"}\n`;
  if (onePager.bullet_points && onePager.bullet_points.length > 0) {
    onePager.bullet_points.forEach(p => (report += `‚Ä¢ ${p}\n`));
  }
  report += `\nCTA: ${onePager.cta || "N/A"}\n\n`;

  // === Proposal ===
  const proposal = data.proposal || {};
  report += `üìë PROPOSAL\n`;
  report += `Introduction: ${proposal.introduction || proposal.intro || "N/A"}\n\n`;

  // Scope
  if (proposal.scope) {
    report += `Scope:\n`;
    if (Array.isArray(proposal.scope)) {
      proposal.scope.forEach(s => (report += `- ${s}\n`));
    } else {
      report += `- ${proposal.scope}\n`;
    }
  }

  // Timeline
  if (proposal.timeline) {
    report += `\nTimeline:\n`;
    if (Array.isArray(proposal.timeline)) {
      proposal.timeline.forEach(t => (report += `- ${t}\n`));
    } else {
      report += `- ${proposal.timeline}\n`;
    }
  }

  report += `\nNext Steps:\n${proposal.next_steps || "N/A"}\n\n`;
  report += "===================================\n";

  // ‚úÖ Convert text to binary data (no imports needed)
  const buffer = Buffer.from(report, "utf8");

  return {
    json: {
      fileName: `Lead_Report_${index + 1}.txt`,
    },
    binary: {
      data: {
        data: buffer.toString("base64"),
        mimeType: "text/plain",
        fileName: `Lead_Report_${index + 1}.txt`,
      },
    },
  };
});

// ‚úÖ FIXED: Correct way to reference previous node data in n8n

// Get all leads from the "Filter for Booked Leads" node
// Use $items() function to get all items from a specific node
const allLeads = $('Filter for Booked Leads').all();

// OR if that doesn't work, use this alternative:
// const allLeads = $input.all();

console.log("Total leads from Filter node:", allLeads.length);
console.log("Total files uploaded:", items.length);

return items.map((item, index) => {

  // 1Ô∏è‚É£ Get file link from current Upload file node output
  const fileLink = 
    item.json.webViewLink || 
    item.json.webContentLink || 
    null;

  const fileName = item.json.name || `Lead_Report_${index + 1}.txt`;

  // 2Ô∏è‚É£ Match with corresponding lead data by index
  const leadData = allLeads[index]?.json || {};

  // 3Ô∏è‚É£ Extract lead information
  const email = leadData.Email || null;
  const companyName = leadData["Company Name"] || "N/A";
  const contactPerson = leadData["Contact Person"] || "N/A";
  const jobTitle = leadData["Job Title"] || "N/A";

  // 4Ô∏è‚É£ Debug logging
  console.log(`Processing item ${index + 1}:`, {
    email,
    companyName,
    hasFileLink: !!fileLink
  });

  // 5Ô∏è‚É£ Validation
  if (!email || !fileLink) {
    return { 
      json: { 
        skip: true, 
        message: `Missing data for ${companyName || 'Unknown Company'}`,
        details: {
          itemIndex: index + 1,
          hasEmail: !!email,
          hasFileLink: !!fileLink,
          fileName: fileName,
          email: email || "MISSING",
          fileLink: fileLink || "MISSING"
        }
      } 
    };
  }

  // 6Ô∏è‚É£ Return complete data for Sheet update
  return {
    json: {
      Email: email,
      ProposalLink: fileLink,
      FileName: fileName,
      CompanyName: companyName,
      ContactPerson: contactPerson,
      JobTitle: jobTitle,
      UpdatedAt: new Date().toISOString(),
      skip: false
    }
  };
});

let uploadedFiles = [];
let totalLeads = 0;

// ‚úÖ Array for structured file data (for downstream use)
let fileList = [];

for (const item of items) {
  // 1Ô∏è‚É£ Get the real file name
  const fileName =
    item.json.name ||
    item.binary?.data?.fileName ||
    item.json.originalFilename ||
    "Unnamed_Report.txt";

  // 2Ô∏è‚É£ Get the Drive link
  const link =
    item.json.webViewLink ||
    item.json.webContentLink ||
    null;

  // 3Ô∏è‚É£ If valid, push into both arrays
  if (link) {
    uploadedFiles.push(
      `<li><a href="${link}" target="_blank" style="color:#1a73e8; text-decoration:none; font-weight:bold;">${fileName}</a></li>`
    );

    fileList.push({
      fileName,
      link,
    });

    totalLeads++;
  }
}

// 4Ô∏è‚É£ Build HTML list
const fileListText =
  uploadedFiles.length > 0
    ? `<ul>${uploadedFiles.join("")}</ul>`
    : "<p>No files were uploaded for this report.</p>";

// 5Ô∏è‚É£ Return everything
return [
  {
    json: {
      totalLeads,
      fileList,
      fileListText,
    },
  },
];
