// 4 MiB chunks (FileFlows UI)
const CHUNK_SIZE = $("Configuration").first().json.chunk_size;

const item = items[0];

// Get the file uploaded by the Form Trigger
// (the field is named "file" â†’ binary.file; fallback to the first binary found)
const bin =
  item.binary?.file ||
  (item.binary ? item.binary[Object.keys(item.binary)[0]] : null);

if (!bin?.data) {
  throw new Error('No file received. Make sure the field is named "file".');
}

const override =
  item.json?.fields?.fileNameOverride?.toString().trim() ||
  item.json?.fileNameOverride?.toString().trim() ||
  '';

const fileName = override || bin.fileName || 'upload.bin';
const mimeType = bin.mimeType || 'application/octet-stream';

const buf = Buffer.from(bin.data, 'base64');
const totalChunks = Math.max(1, Math.ceil(buf.length / CHUNK_SIZE));

const out = [];
for (let i = 0; i < totalChunks; i++) {
  const start = i * CHUNK_SIZE;
  const end = Math.min(start + CHUNK_SIZE, buf.length);
  const slice = buf.subarray(start, end);

  out.push({
    json: {
      fileName,
      chunkNumber: i,       // 0-based like the UI
      totalChunks,
      size: slice.length
    },
    binary: {
      chunk: {
        data: slice.toString('base64'),
        fileName,
        mimeType
      }
    }
  });
}

return out;

const binaries = $input.first().binary;
const entries = Object.entries(binaries);

const Audio = [];
for (let index = 0; index < entries.length; index++) {
  const [key, value] = entries[index];

  Audio.push({
    json: {
      fileExtension: value.fileExtension,
      fileName: value.fileName,
      fileSize: value.fileSize,
      fileType: value.fileType,
      mimeType: value.mimeType,
    },
    binary: {
      ["Audio"]: value, // Preserve the complete binary structure
    },
  });
}

// âš ï¸ Important : send back array of items
return Audio;

let res = "";
// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  res += item.json.text;
}

return {
  transcription: res
};
