// Access the binary data from the previous node
const binaryData = $input.first().binary?.data;

if (!binaryData) {
  throw new Error("No binary data found");
}

// binaryData.data is the Base64-encoded string of the image file
const base64Image = binaryData.data;

// You can now use this base64Image string as needed,
// for example, send it to another service or include it in JSON output

return {
  json: {
    base64Image: base64Image,
  },
  binary: {
    data: binaryData, // optionally keep the binary data for next nodes
  }
};
