let source = 'unknown';

if ($json.message && $json.message.chat && $json.message.from) {
  source = 'telegram';
} else if ($json.threadId && $json.id && $json.from && $json.subject) {
  source = 'gmail';
} else if ($json['Your Email'] && $json["What's your feedback?"]) {
  source = 'google_forms';
}

let normalized = {};

if (source === 'telegram') {
  const msg = $json.message;
  normalized = {
    id: `tg_${msg.from.id}_${Date.now()}`,
    source: 'telegram',
    timestamp: new Date().toISOString(),
    user: {
      telegram_id: msg.from.id,
      name: msg.from.first_name + (msg.from.last_name ? ' ' + msg.from.last_name : ''),
      username: msg.from.username || null,
      email: null, 
      tier: 'free', 
    },
    content: {
      raw_text: msg.text,
      subject: null,
    },
    metadata: {
      chat_id: msg.chat.id,
      message_id: msg.message_id,
      language: msg.from.language_code || 'en',
    },
  };
}
else if (source === 'gmail') {
  normalized = {
    id: `email_${$json.id}`,
    source: 'gmail',
    timestamp: $json.receivedAt,
    user: {
      email: $json.from,
      name: $json.from.split('@')[0],
      tier: 'free', 
    },
    content: {
      raw_text: $json.textPlain || $json.textHtml,
      subject: $json.subject,
    },
    metadata: {
      message_id: $json.id,
      thread_id: $json.threadId,
    },
  };
}
else if (source === 'google_forms') {
  normalized = {
    id: `form_${Date.now()}`,
    source: 'google_forms',
    timestamp: $json.Timestamp,
    user: {
      email: $json['Your Email'],
      name: $json['Your Email'].split('@')[0],
      tier: $json['User Tier'] || 'free',
    },
    content: {
      raw_text: $json["What's your feedback?"],
      subject: $json['Category'],
      category_hint: $json['Category'], 
      urgency_hint: $json['How urgent?'], 
    },
    metadata: {
      form_response: true,
    },
  };
} else {

  normalized = {
    id: `unknown_${Date.now()}`,
    source,
    raw: $json,
  };
}


return [
  {
    json: normalized,
  },
];

const tierMapping = {
  'user@example.com': 'enterprise',
  'user@example.com': 'enterprise',
  'user@example.com': 'pro',
};

const user = $json.user || {};

const email = user.email || null;
let tier = user.tier || 'free'; 

if (email && tierMapping[email]) {
  tier = tierMapping[email];
}

const tierWeights = {
  enterprise: 2.0,
  pro: 1.5,
  free: 1.0,
};

const safeTierKey = (tier || 'free').toLowerCase();

return {
  ...$json,
  user: {
    ...user,
    tier,
    tier_weight: tierWeights[safeTierKey] || 1.0,
  },
};

const ai = $json.ai_analysis;
const tier_weight = $json.user?.tier_weight || 1.0;

const pain = ai.pain_level;
const impact = ai.business_impact;
const effort = ai.implementation_effort;

const frequency = 10;

const weighted_impact = (pain * 0.4 + impact * 0.6) * tier_weight;

const rice_score = (weighted_impact * frequency) / effort;

let priority;

if (rice_score >= 15 || (ai.category === 'bug' && pain >= 8)) {
  priority = 'P0';
} else if (rice_score >= 10) {
  priority = 'P1';
} else if (rice_score >= 5) {
  priority = 'P2';
} else {
  priority = 'P3';
}


const jiraPriorityNameMap = {
  P0: 'Highest',
  P1: 'High',
  P2: 'Medium',
  P3: 'Low', 
};

const jira_priority = jiraPriorityNameMap[priority] || 'Low';


const jiraPriorityIdMap = {
  Highest: '1',
  High: '2',
  Medium: '3',
  Low: '4',
  Lowest: '5',
};

const jira_priority_id = jiraPriorityIdMap[jira_priority] || jiraPriorityIdMap['Low'];

return {
  ...$json,
  scoring: {
    rice_score: Math.round(rice_score * 10) / 10,
    weighted_impact: Math.round(weighted_impact * 10) / 10,
    frequency,
    priority,          
    jira_priority,     
    jira_priority_id,  
    components: {
      pain,
      business_impact: impact,
      implementation_effort: effort,
      tier_weight,
    },
  },
};

