const items = $input.all().map(item => {
  let aiResponseText = item.json.message?.content || '';

  aiResponseText = aiResponseText
    .replace(/```json\s*/gi, '')
    .replace(/```\s*/g, '')
    .trim();

  let aiAnalysis;

  try {
    aiAnalysis = JSON.parse(aiResponseText);
  } catch (error) {
    console.error('JSON Parse Error:', error);
    console.error('Raw response:', aiResponseText);

    aiAnalysis = {
      category: 'question',
      tags: ['needs-review'],
      sentiment: 'neutral',
      pain_level: 5,
      business_impact: 5,
      implementation_effort: 5,
      summary: 'Parse error - manual review needed',
      description: aiResponseText.substring(0, 200),
      acceptance_criteria: ['Manual review required'],
    };
  }

  return {
    json: {
      ...item.json,        
      ai_analysis: aiAnalysis,
    },
  };
});

return items;

