const jiraResponse = $json;
const originalData = $input.first().json;

return {
  ...originalData,
  jira: {
    key: jiraResponse.key,
    id: jiraResponse.id,
    url: `https://yourspace.atlassian.net/browse/${jiraResponse.key}`,
    self: jiraResponse.self
  }
};

const allFeedbacks = $input.all().map(item => item.json);

const now = new Date();
const monthStart = new Date(now.getFullYear(), now.getMonth(), 1);
const monthEnd = new Date(now.getFullYear(), now.getMonth() + 1, 0);

const monthName = monthStart.toLocaleString('default', { 
  month: 'long', 
  year: 'numeric' 
});

const monthlyFeedbacks = allFeedbacks.filter(fb => {
  if (!fb || !fb.Timestamp) {
    return false;
  }


  const safeTimestamp = String(fb.Timestamp).replace(" ", "T");
  const fbDate = new Date(safeTimestamp);

  if (isNaN(fbDate)) {
    return false;
  }

  return fbDate >= monthStart && fbDate <= monthEnd;
});

if (monthlyFeedbacks.length === 0) {
  const summary = `Monthly feedback report â€“ ${monthName}\n\nNo feedbacks found for this period.`;

  return {
    month: monthName,
    stats: {
      month: monthName,
      date_range: {
        start: monthStart.toISOString().split('T')[0],
        end: monthEnd.toISOString().split('T')[0],
      },
      total: 0,
      by_category: {},
      by_sentiment: {},
      by_priority: {},
      by_source: {},
      by_tier: {},
      avg_rice_score: 0,
      avg_pain_level: 0,
      avg_business_impact: 0,
      p0_p1_count: 0,
      p0_p1_rate: '0%',
      top_pain_points: [],
      top_feature_requests: [],
      bug_patterns: {},
      sentiment_score: 0,
      top_category: 'none',
      note: 'No feedbacks found for this period',
    },
    raw_feedbacks: [],
    summary_for_notion: summary.slice(0, 1900),
  };
}

const stats = {
  month: monthName,
  date_range: {
    start: monthStart.toISOString().split('T')[0],
    end: monthEnd.toISOString().split('T')[0]
  },
  total: monthlyFeedbacks.length,
  by_category: {},
  by_sentiment: {},
  by_priority: {},
  by_source: {},
  by_tier: {},
  avg_rice_score: 0,
  avg_pain_level: 0,
  avg_business_impact: 0,
  p0_p1_count: 0,
  top_pain_points: [],
  top_feature_requests: [],
  bug_patterns: {}
};

let totalRice = 0, totalPain = 0, totalImpact = 0;

monthlyFeedbacks.forEach(fb => {
  const cat = fb.Category || 'unknown';
  stats.by_category[cat] = (stats.by_category[cat] || 0) + 1;

  const sent = fb.Sentiment || 'neutral';
  stats.by_sentiment[sent] = (stats.by_sentiment[sent] || 0) + 1;

  const prio = fb.Priority || 'P3';
  stats.by_priority[prio] = (stats.by_priority[prio] || 0) + 1;

  const src = fb.Source || 'unknown';
  stats.by_source[src] = (stats.by_source[src] || 0) + 1;

  const tier = fb['User Tier'] || 'free';
  stats.by_tier[tier] = (stats.by_tier[tier] || 0) + 1;

  const rice = parseFloat(fb['RICE Score']) || 0;
  const pain = parseFloat(fb['Pain Level']) || 0;
  const impact = parseFloat(fb['Business Impact']) || 0;

  totalRice += rice;
  totalPain += pain;
  totalImpact += impact;

  if (prio === 'P0' || prio === 'P1') {
    stats.p0_p1_count++;
  }

  if (pain >= 8) {
    stats.top_pain_points.push({
      summary: fb['AI Summary'],
      pain,
      rice,
      tier,
      jira: fb['Jira Ticket']
    });
  }

  if (cat === 'feature_request') {
    stats.top_feature_requests.push({
      summary: fb['AI Summary'],
      rice,
      tier,
      jira: fb['Jira Ticket']
    });
  }

  if (cat === 'bug') {
    const tags = (fb.Tags || '').split(',').map(t => t.trim());
    tags.forEach(tag => {
      if (tag) {
        stats.bug_patterns[tag] = (stats.bug_patterns[tag] || 0) + 1;
      }
    });
  }
});

stats.avg_rice_score = (totalRice / monthlyFeedbacks.length).toFixed(2);
stats.avg_pain_level = (totalPain / monthlyFeedbacks.length).toFixed(1);
stats.avg_business_impact = (totalImpact / monthlyFeedbacks.length).toFixed(1);
stats.p0_p1_rate = ((stats.p0_p1_count / monthlyFeedbacks.length) * 100).toFixed(1) + '%';

stats.top_pain_points.sort((a, b) => b.rice - a.rice);
stats.top_pain_points = stats.top_pain_points.slice(0, 10);

stats.top_feature_requests.sort((a, b) => b.rice - a.rice);
stats.top_feature_requests = stats.top_feature_requests.slice(0, 10);

const sentimentWeights = { 
  positive: 10, 
  neutral: 5, 
  negative: 0 
};

let sentimentSum = 0;
Object.keys(stats.by_sentiment).forEach(s => {
  const weight = sentimentWeights[s] || 5;
  sentimentSum += stats.by_sentiment[s] * weight;
});
stats.sentiment_score = (sentimentSum / monthlyFeedbacks.length).toFixed(1);

const topCategory = Object.entries(stats.by_category)
  .sort((a, b) => b[1] - a[1])[0];

stats.top_category = topCategory ? 
  `${topCategory[0]} (${topCategory[1]})` : 'none';

const summaryLines = [
  `Monthly feedback report â€“ ${monthName}`,
  ``,
  `â€¢ Total feedbacks: ${stats.total}`,
  `â€¢ Top category: ${stats.top_category}`,
  `â€¢ Avg RICE score: ${stats.avg_rice_score}`,
  `â€¢ Avg pain level: ${stats.avg_pain_level}`,
  `â€¢ Avg business impact: ${stats.avg_business_impact}`,
  `â€¢ P0/P1 ratio: ${stats.p0_p1_rate}`,
];

const summaryText = summaryLines.join('\n');
const notionSummary = summaryText.slice(0, 1900);

return {
  month: monthName,
  stats,
  raw_feedbacks: monthlyFeedbacks.slice(0, 50),
  summary_for_notion: notionSummary,
};

let aiText = $json.choices?.[0]?.message?.content;

if (!aiText) {
  throw new Error("No AI content found in OpenAI response.");
}

if (typeof aiText === 'object') {
  return {
    ...$input.first().json,
    ai_insights: aiText
  };
}

aiText = String(aiText);

aiText = aiText
  .replace(/```json/gi, '')
  .replace(/```/g, '')
  .trim();

let insights;
try {
  insights = JSON.parse(aiText);
} catch (e) {
  throw new Error("Failed to parse AI JSON: " + aiText);
}

return {
  ...$input.first().json,
  ai_insights: insights
};

