 remediation: 'Never combine * with credentials'
 });
 }

 // GOOD: Specific origin
 if (acao && acao !== '*' && !acao.includes('evil.com')) {
 findings.passed.push(`CORS restricted to: ${acao}`);
 }

 return { json: findings };

} catch (err) {
 return { json: { test: 'CORS Preflight', error: err.message } };
}

try {
 const allFindings = [];
 const erroredTests = [];
 const allPassed = [];
 const summary = {
 scanId: $('Configuration').item.json.scanId,
 targetUrl: $('Configuration').item.json.targetUrl,
 endpoint: $('Configuration').item.json.endpoint,
 scanDate: new Date().toISOString(),
 totalIssues: 0,
 critical: 0,
 high: 0,
 medium: 0,
 low: 0,
 testsConducted: [],
 totalPassed: 0
 };

 for (const item of $input.all()) {
 const data = item.json;

 if (data.error) {
 erroredTests.push({ test: data.test, error: data.error });
 summary.testsConducted.push(data.test + ' (ERROR)');
 continue;
 }

 if (data.test) {
 summary.testsConducted.push(data.test);
 }

 if (data.passed && data.passed.length > 0) {
 allPassed.push(...data.passed.map(p => ({ test: data.test, check: p })));
 summary.totalPassed += data.passed.length;
 }

 if (data.issues && data.issues.length > 0) {
 for (const issue of data.issues) {
 summary.totalIssues++;
 allFindings.push({
 test: data.test,
 endpoint: data.endpoint,
 url: data.url,
 statusCode: data.statusCode,
 ...issue
 });

 if (issue.severity === 'CRITICAL') summary.critical++;
 else if (issue.severity === 'HIGH') summary.high++;
 else if (issue.severity === 'MEDIUM') summary.medium++;
 else if (issue.severity === 'LOW') summary.low++;
 }
 }
 }

 const severityOrder = { 'CRITICAL': 0, 'HIGH': 1, 'MEDIUM': 2, 'LOW': 3 };
 allFindings.sort((a, b) => severityOrder[a.severity] - severityOrder[b.severity]);

 return [{ json: { summary, findings: allFindings, passed: allPassed, errors: erroredTests } }];
} catch (error) {
 return [{ json: { error: 'Report generation failed', message: error.message } }];
}

const data = $input.item.json;
const findings = data.findings || [];
const critical = findings.filter(f => f.severity === 'CRITICAL');
const high = findings.filter(f => f.severity === 'HIGH');

const markdown = [
 '# Security Scan Report - TOP ISSUES',
 '',
 '## Critical & High Priority',
 ...critical.slice(0, 3).map(f => '### [CRITICAL] ' + f.type + '\n' + f.detail + '\n**Fix:** ' + f.remediation),
 ...high.slice(0, 3).map(f => '### [HIGH] ' + f.type + '\n' + f.detail + '\n**Fix:** ' + f.remediation),
 '',
 '## All Findings',
 ...findings.map(f => '- **' + f.severity + '** [' + f.test + '] ' + f.type + ': ' + f.detail),
 '',
 '**Scan ID:** ' + data.summary.scanId,
 '**Date:** ' + data.summary.scanDate
].join('\n');

return [{ json: { ...data, markdown: markdown } }];
