const body = $json.body || $json;

if (!body.id || !body.customer || !body.items || body.items.length === 0) {
  throw new Error('Invalid order payload');
}

return [{ json: body }];

let payload = $json;
if (payload.body) payload = payload.body;

const order = {
  id: payload.id,
  customer: {
    name: payload.customer?.name || '',
    email: payload.customer?.email || ''
  },
  items: (payload.items || []).map(i => ({
    sku: i.sku || null,
    qty: i.quantity || 1,
    name: i.name || '',
    price: i.price || 0
  })),
  total: payload.total || 0
};

return [{ json: order }];

const orderItems = $json["items"];

return orderItems.map(i => {
  return {
    json: {
      sku: i.sku,
      qty: i.qty
    }
  };
});

return [{ json: {
  invoiceId: 'INV-' + new Date().getTime(),
  orderId: $node["Request Info"].json["id"],
  sku: $node["Item and quantity"].json["sku"],
  qty: $node["Item and quantity"].json["qty"],
  customer: $node["Request Info"].json["customer"]["name"],
  amount: $node["Request Info"].json["total"],
  date: new Date().toISOString()
}}];

return [{ json: {
  order_id: $node["Request Info"].json["id"],
  amount: $node["Request Info"].json["total"],
  status: "Success",
  created_at: new Date().toISOString()
}}];

return [{ json: {
  order_id: $node["Request Info"].json["id"],
  amount: $node["Request Info"].json["total"],
  status: "Out of Stock",
  created_at: new Date().toISOString()
}}];

