// Create output array
const outputItems = [];

// Loop over input items
for (const item of $input.all()) {
  // Get the string and clean it
  let jsonString = item.json.output;

  // Remove ```json and ``` markers
  jsonString = jsonString.replace(/```json\n?/g, '').replace(/```\n?/g, '');

  // Parse the JSON
  const parsedData = JSON.parse(jsonString);

  // Create an item for each article
  for (const article of parsedData) {
    outputItems.push({
      json: {
        title: article.title,
        link: article.link,
        summary: article.summary
      }
    });
  }
}

return outputItems;

