/**
 * POST-LMM JOIN (NO FILTERING)
 * Expects (after MERGE):
 * - $json.pre_extracted : array base items
 * - $json.output : string with JSON array from LLM
 *
 * Returns: ALL items (one n8n item per product) with enrichment fields.
 */

function stripCodeFences(s) {
  if (typeof s !== "string") return s;
  const t = s.trim();
  if (!t.startsWith("```")) return t;
  let body = t.replace(/^```(?:json)?\s*/i, "");
  if (body.endsWith("```")) body = body.slice(0, -3);
  return body.trim();
}

function tryParseJSON(text) {
  if (Array.isArray(text)) return text;
  if (typeof text !== "string") return null;
  try { return JSON.parse(text); } catch {}
  const m = text.match(/(\[[\s\S]*\]|\{[\s\S]*\})/);
  if (m) {
    try { return JSON.parse(m[1]); } catch {}
  }
  return null;
}

function clamp(n, min, max) {
  return Math.max(min, Math.min(max, n));
}

function toNumberOrNull(v) {
  if (v === null || v === undefined) return null;
  if (typeof v === "number") return Number.isFinite(v) ? v : null;
  const s = String(v);
  const m = s.match(/(\d{1,6}(?:[.,]\d{1,2})?)/);
  if (!m) return null;
  const f = parseFloat(m[1].replace(",", "."));
  return Number.isFinite(f) ? f : null;
}

// thresholds (4 categor√≠as)
const thresholdsByCategory = { tech: 30, home: 20, fashion: 15, other: 20 };

// base items (from pre-extract)
const base = Array.isArray($json.pre_extracted) ? $json.pre_extracted : [];

// LLM output (string) -> array
const llmStr = $json.output || "";
const enrichedArr = tryParseJSON(stripCodeFences(llmStr)) || [];
const enrichedList = Array.isArray(enrichedArr) ? enrichedArr : [enrichedArr];

// index enriched by listing_id
const enrichedById = new Map();
for (const e of enrichedList) {
  const id = (e?.listing_id ?? "").toString().trim();
  if (!id) continue;

  let category_ai = (e?.category_ai ?? "other").toString().trim().toLowerCase();
  if (!["tech", "home", "fashion", "other"].includes(category_ai)) category_ai = "other";

  let deal_score = toNumberOrNull(e?.deal_score);
  deal_score = deal_score === null ? 5 : clamp(deal_score, 0, 10);

  const score_reason = (e?.score_reason ?? "")
    .toString()
    .trim()
    .replace(/\s+/g, " ")
    .slice(0, 120) || "No reason provided.";

  enrichedById.set(id, { category_ai, deal_score, score_reason });
}

const nowISO = new Date().toISOString();

const out = [];
const seen = new Set();

for (const it of base) {
  const listing_id = (it?.listing_id ?? "").toString().trim();
  if (!listing_id || seen.has(listing_id)) continue;
  seen.add(listing_id);

  const title = (it?.title ?? null)?.toString()?.trim() || null;
  const price = toNumberOrNull(it?.price);
  const currency = (it?.currency ?? null)?.toString()?.trim()?.toUpperCase() || null;
  const original_price = toNumberOrNull(it?.original_price);
  const url = (it?.url ?? null)?.toString()?.trim() || null;
  const image_url = (it?.image_url ?? null)?.toString()?.trim() || null;

  const enrich = enrichedById.get(listing_id) || {
    category_ai: "other",
    deal_score: 5,
    score_reason: "No LLM enrichment (fallback)."
  };

  const effective_threshold = thresholdsByCategory[enrich.category_ai] ?? 20;
  const price_below_threshold = Boolean(currency === "EUR" && price !== null && price < effective_threshold);

  // discount %
  let discount_pct = null;
  if (price !== null && original_price !== null && original_price > 0 && original_price > price) {
    discount_pct = clamp(((original_price - price) / original_price) * 100, 0, 99.9);
    discount_pct = Math.round(discount_pct * 10) / 10;
  }

  out.push({
    listing_id,
    title,
    price,
    currency,
    original_price,
    discount_pct,
    url,
    image_url,

    category_ai: enrich.category_ai,
    deal_score: enrich.deal_score,
    score_reason: enrich.score_reason,

    effective_threshold,
    price_below_threshold,

    scraped_at: nowISO
  });
}

// ‚úÖ DEVOLVEMOS TODOS, SIN FILTRAR
return out.map(x => ({ json: x }));

