/**
 * PRE-EXTRACT for eBay Deals (dne-itemtile blocks)
 * Input:  $json.results[0].content (HTML)
 * Output: compact_text + pre_extracted + stats
 */

function decodeHtml(s) {
  if (!s || typeof s !== "string") return s;
  return s
    .replace(/&amp;/g, "&")
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/&lt;/g, "<")
    .replace(/&gt;/g, ">")
    .replace(/&#(\d+);/g, (_, code) => String.fromCharCode(Number(code)));
}

function cleanText(s, maxLen = 200) {
  if (!s) return null;
  return decodeHtml(String(s))
    .replace(/<[^>]*>/g, " ")
    .replace(/\s+/g, " ")
    .trim()
    .slice(0, maxLen) || null;
}

function parsePriceNumber(priceStr) {
  if (!priceStr) return null;
  // "245,25 EUR" -> 245.25 | "19,99 ‚Ç¨" -> 19.99
  const raw = String(priceStr).trim();
  const m = raw.match(/(\d{1,4}(?:[.,]\d{1,2})?)/);
  if (!m) return null;
  const n = m[1].replace(",", ".");
  const f = parseFloat(n);
  return Number.isFinite(f) ? f : null;
}

function normalizeCurrency(curStr, fallbackChunk = "") {
  if (curStr) {
    const u = String(curStr).trim().toUpperCase();
    if (u.includes("EUR")) return "EUR";
    if (u.includes("USD")) return "USD";
    return u;
  }
  if (fallbackChunk.includes("‚Ç¨") || /\bEUR\b/i.test(fallbackChunk)) return "EUR";
  if (fallbackChunk.includes("$") || /\bUSD\b/i.test(fallbackChunk)) return "USD";
  return null;
}

function normalizeUrl(url) {
  if (!url) return null;
  if (url.startsWith("http")) return url;
  if (url.startsWith("//")) return "https:" + url;
  if (url.startsWith("/")) return "https://www.ebay.es" + url;
  return "https://www.ebay.es/" + url.replace(/^\s+/, "");
}

const html = $json?.results?.[0]?.content;
if (!html || typeof html !== "string") {
  return [{ json: { compact_text: "NO_HTML_INPUT", pre_extracted: [], stats: { html_len: 0, items: 0 } } }];
}

const htmlLen = html.length;

// Capturamos bloques que tengan data-listing-id y la clase dne-itemtile
// Nota: el HTML es grande; hacemos un match ‚Äúcontenedor‚Äù razonable alrededor del listing.
const blockRegex = /data-listing-id=(\d{9,15})[\s\S]{0,6000}?class="[^"]*dne-itemtile[^"]*"|class="[^"]*dne-itemtile[^"]*"[\s\S]{0,6000}?data-listing-id=(\d{9,15})/g;

const items = [];
const seen = new Set();

let match;
while ((match = blockRegex.exec(html)) !== null) {
  const listing_id = match[1] || match[2];
  if (!listing_id || seen.has(listing_id)) continue;
  seen.add(listing_id);

  // Cogemos una ‚Äúventana‚Äù alrededor del match para parsear detalles del tile
  const idx = match.index;
  const start = Math.max(0, idx - 1500);
  const end = Math.min(html.length, idx + 6000);
  const chunk = html.slice(start, end);

  // URL del item
  let url = null;
  // suele venir: href=https://www.ebay.es/itm/336061318758?... (a veces sin comillas)
  const urlMatch =
    chunk.match(new RegExp(`href=([^\\s>]{0,300}/itm/${listing_id}[^\\s>]*)`, "i")) ||
    chunk.match(new RegExp(`href="([^"]{0,300}/itm/${listing_id}[^"]*)"`, "i"));
  if (urlMatch) url = normalizeUrl(urlMatch[1]);
  if (!url) url = `https://www.ebay.es/itm/${listing_id}`;

  // Title: suele venir en h3 title="..."
  let title = null;
  const titleMatch = chunk.match(/dne-itemtile-title[\s\S]{0,400}?title="([^"]{3,220})"/i);
  if (titleMatch) title = cleanText(titleMatch[1], 220);

  // Currency: meta itemprop=priceCurrency content=EUR
  let currency = null;
  const curMatch = chunk.match(/itemprop=priceCurrency\s+content=([A-Z]{3})/i);
  if (curMatch) currency = normalizeCurrency(curMatch[1], chunk);

  // Price: <span itemprop=price class=first>245,25 EUR</span>
  let price_text = null;
  const priceMatch = chunk.match(/itemprop=price[^>]*>\s*([^<]{1,40})</i);
  if (priceMatch) price_text = cleanText(priceMatch[1], 40);

  const price = parsePriceNumber(price_text);
  currency = normalizeCurrency(currency, price_text || chunk);

  // Original price (si existe): itemtile-price-strikethrough>327,00 EUR
  let original_price_text = null;
  const origMatch = chunk.match(/itemtile-price-strikethrough[^>]*>\s*([^<]{1,40})</i);
  if (origMatch) original_price_text = cleanText(origMatch[1], 40);
  const original_price = parsePriceNumber(original_price_text);

  // Image url (suele ser: <img src=https://i.ebayimg.com/images/...>
  let image_url = null;
  const imgMatch =
    chunk.match(/<img\s+[^>]*src=([^\s>]+ebayimg\.com[^\s>]+)\s/i) ||
    chunk.match(/<img\s+[^>]*src="([^"]+ebayimg\.com[^"]+)"/i);
  if (imgMatch) image_url = normalizeUrl(imgMatch[1]);

  items.push({
    listing_id,
    title,
    price,
    currency,
    price_text,
    original_price,
    original_price_text,
    url,
    image_url,
  });

  if (items.length >= 60) break;
}

// compact payload para el LLM (barato en tokens)
const lines = [];
lines.push("SOURCE: eBay Deals (pre-extracted from dne-itemtile)");
lines.push(`ITEMS: ${items.length}`);
lines.push("---");
for (const it of items) {
  lines.push(
    [
      `ID=${it.listing_id}`,
      `TITLE=${it.title ?? "null"}`,
      `PRICE=${it.price ?? "null"}`,
      `CURRENCY=${it.currency ?? "null"}`,
      `ORIGINAL_PRICE=${it.original_price ?? "null"}`,
      `URL=${it.url ?? "null"}`,
      `IMG=${it.image_url ?? "null"}`,
    ].join(" | ")
  );
}

return [
  {
    json: {
      compact_text: lines.join("\n"),
      pre_extracted: items,
      stats: { html_len: htmlLen, items: items.length, unique_ids: seen.size },
    },
  },
];
