// === Daily Email Counter with Weekend Exclusion & Category Tracking ===

// ‚úÖ 1. Get today's date and day of week
const now = new Date();
const today = now.toISOString().split('T')[0];
const day = now.getDay(); // 0 = Sunday, 6 = Saturday

// ‚úÖ 2. Skip weekends automatically
if (day === 0 || day === 6) {
  console.log("üö´ Weekend detected. Skipping all email sends today.");
  return []; // Stop workflow on weekends
}

// ‚úÖ 3. Set your total daily Gmail quota
const totalLimit = 100;  // adjust if needed

// ‚úÖ 4. Access persistent storage (shared across all workflows)
const data = $getWorkflowStaticData('global');

// ‚úÖ 5. Reset if new day
if (!data.sentCountDate || data.sentCountDate !== today) {
  data.sentCountDate = today;
  data.sentCount = 0;
  data.breakdown = { initial: 0, retarget1: 0, retarget2: 0, reply: 0 };
}

// ‚úÖ 6. Read email type passed from the Set node
const emailType = $json.emailType || 'unknown';

// ‚úÖ 7. Stop all if total daily quota is reached
if (data.sentCount >= totalLimit) {
  console.log(`üö´ Daily Gmail quota reached (${data.sentCount}/${totalLimit}).`);
  return [];
}

// ‚úÖ 8. Define category-specific ratios (adjust to your liking)
const ratios = {
  initial: 0.55,   // 55% for new hot leads
  retarget1: 0.20, // 20% for first follow-up
  retarget2: 0.15, // 15% for second follow-up
  reply: 0.10,     // 10% for ongoing replies
};

// ‚úÖ 9. Compute local quota per category
const localLimit = Math.floor(totalLimit * (ratios[emailType] || 0.25));

// ‚úÖ 10. Skip sending if that category is full
if (data.breakdown[emailType] >= localLimit) {
  console.log(`‚è∏ ${emailType} quota full (${localLimit} max).`);
  return [];
}

// ‚úÖ 11. Increment counters
data.sentCount += 1;
if (!data.breakdown[emailType]) data.breakdown[emailType] = 0;
data.breakdown[emailType] += 1;

// ‚úÖ 12. Log updates
console.log(`üì© Sent #${data.sentCount}/${totalLimit} | Type: ${emailType}`);
console.log(`üìä Breakdown: ${JSON.stringify(data.breakdown)}`);

// ‚úÖ 13. Allow sending to continue
return items;

// Generate a random delay between 0 and 60 seconds (0‚Äì2 minutes)
const delay = Math.floor(Math.random() * 121); // random 0‚Äì60 seconds

// Return delay in milliseconds so n8n understands
