};

// Full email cleanup + validation + precise block rules
return items.map(item => {
  let email = (item.json.EMAIL || "").trim().toLowerCase();
  email = email.replace(/[.,]+$/, ""); // remove trailing dots/commas
  item.json.EMAIL = email;

  // Step 1: Strict syntax validation
  const validEmail =
    /^(?!.*\.\.)([a-zA-Z0-9._%+-]+)@([a-zA-Z0-9.-]+\.[A-Za-z]{2,})$/;
  const match = email.match(validEmail);
  const domain = match ? match[2] : "";

  // Step 2: Blocked full domains
  const blockedDomains = [
    "coingecko.com",
    "coinmarketcap.com",
    "pinksale.finance",
    "pump.fun",
    "domain.com",
    "mysite.com",
    "facebook.com",
    "instagram.com",
    "linkedin.com",
    "twitter.com",
    "binance.com",
    "mexc.com",
    "tiktok.com",
  ];

  // Step 3: Blocked partial or exact keyword matches
  // "@x.com" specifically blocks real X (Twitter) addresses, not random ".x.com" strings
  const blockedKeywords = [
    "pumpfun", // covers subdomains like "team.pumpfun.io"
    "@x.com",  // only blocks if "@x.com" appears directly in email
  ];

  const isBlocked =
    blockedDomains.includes(domain) ||
    blockedKeywords.some(k => email.includes(k.toLowerCase()));

  const isValidFormat = !!match;
  const isValid = isValidFormat && !isBlocked;

  // Step 4: Explanation fields
  item.json.valid = isValid;
  item.json.blocked = isBlocked;
  item.json.reason = !isValidFormat
    ? "Invalid format"
    : isBlocked
    ? "Blocked keyword/domain"
    : "Valid email";

  return item;
});

function pickRandom(arr) {
  return arr[Math.floor(Math.random() * arr.length)];
}

return items.map(item => {
  /* -------- SUBJECT LINES -------- */
  const subjectPool = [
    "Quick question",
    "Testing a new outreach workflow",
    "Short introduction",
    "Reaching out briefly",
    "Exploring a quick connection"
  ];

  /* -------- OPENING LINES -------- */
  const openingLinePool = [
    "I hope you‚Äôre having a great day.",
    "Just a quick note to introduce myself.",
    "I wanted to briefly reach out and say hello."
  ];

  /* -------- MAIN BODY (SALES / TEST) -------- */
  const bodyPool = [
    "This is a short outreach message as part of testing a lightweight email automation workflow. No action is required ‚Äî just validating delivery and formatting.",
    "I‚Äôm currently testing a simple outreach system designed for clean, low-volume communication. This message is part of that test.",
    "This email is part of a small test to confirm that a new outreach workflow is working as expected."
  ];

  /* -------- CLOSING LINES -------- */
  const closingLinePool = [
    "Thanks for your time.",
    "Appreciate you reading this."
  ];

  /* -------- SIGNATURE -------- */
  const senderName = item.json["Sender Name"] || "Your Name";

  const signature = `
<p style="margin-top:14px;font-family:Arial,Helvetica,sans-serif;font-size:14px;">
Yours sincerely,<br>
<strong>${senderName}</strong>
</p>
`;

  return {
    json: {
      ...item.json,
      Subject: pickRandom(subjectPool),
      Greeting: `Hi ${item.json.Name || "there"},`,
      Opening: pickRandom(openingLinePool),
      Body: pickRandom(bodyPool),
      Closing: pickRandom(closingLinePool),
      Signature: signature
    }
  };
});

