// SINGLE CODE NODE ‚Üí Certificate ID + QR Code (perfect for n8n 1.117.3)
const body = items[0].json.body;

// Generate Certificate ID
const certId = `CERT-CEU-${Date.now()}-${Math.random().toString(36).substr(2, 6).toUpperCase()}`;
const verificationUrl = `https://training.verify.com/cert?id=${certId}`;

// Direct QR code URL from qrserver.com
const qrCodeUrl = `https://api.qrserver.com/v1/create-qr-code/?size=300x300&format=png&margin=10&data=${encodeURIComponent(verificationUrl)}`;

return [
  {
    json: {
      ...body,
      certificateId: certId,
      verificationUrl: verificationUrl,
      qrCodeUrl: qrCodeUrl,
      generatedAt: new Date().toISOString()
    },
    // This tells n8n to download the QR image automatically and embed it
    binary: {
      qrCode: {
        data: qrCodeUrl,
        mimeType: "image/png",
        fileName: `${certId}-QR.png`,
        fileExtension: "png"
      }
    }
  }
];
