// Extract only the actual payload from the webhook
const payload = $input.first().json.body;

// Required fields
const required = ["name", "email", "event"];
const errors = [];

// Check required fields
required.forEach(field => {
  if (!payload[field] || String(payload[field]).trim() === "") {
    errors.push(`${field} is required`);
  }
});

// Very basic email format check
if (!payload.email || !payload.email.includes("@") || !payload.email.includes(".")) {
  errors.push("Invalid email format");
}

if (errors.length > 0) {
  throw new Error("VALIDATION_ERROR: " + errors.join(" | "));
}

// Pass only the clean data forward
return [{ json: payload }];

const now = new Date();
const badgeId = `HACK-${now.getFullYear()}-${Math.floor(Date.now()/1000)}-${Math.random().toString(36).substr(2,6).toUpperCase()}`;
const verifyUrl = `https://yourdomain.com/verify-badge?id=${badgeId}`;
const issuedAt = now.toLocaleDateString('en-US', {
  month: 'long',
  day: 'numeric',
  year: 'numeric'
});

// Direct QR code URL (no external node needed!)
const qrCodeUrl = `https://api.qrserver.com/v1/create-qr-code/?size=300x300&format=png&data=${encodeURIComponent(verifyUrl)}`;

return [{
  json: {
    ...$json,
    badgeId,
    verifyUrl,
    issuedAt,
    qrCodeUrl
  }
}];

