// Extract calendar event data from Get Next Calendar Event node
const calendarEvent = $('Get Next Calendar Event').first().json;

// Extract travel time data from Google Maps API response
const travelData = $('Get Travel Time (Google Maps API)').first().json;

// Get travel time in seconds from the API response
const travelTimeSeconds = travelData.rows?.[0]?.elements?.[0]?.duration?.value || 0;

// Calculate travel time in minutes
const travelTimeMinutes = Math.ceil(travelTimeSeconds / 60);

// Get event start time
const eventStartTime = calendarEvent.start?.dateTime || calendarEvent.start?.date;

// Calculate time until event starts (in minutes)
const currentTime = new Date();
const eventStart = new Date(eventStartTime);
const timeUntilEventMinutes = Math.floor((eventStart - currentTime) / (1000 * 60));

// Calculate gap time (time until event minus travel time)
const gapTimeMinutes = timeUntilEventMinutes - travelTimeMinutes;

// Return the calculated data
return [
  {
    json: {
      gapTimeMinutes: gapTimeMinutes,
      travelTimeMinutes: travelTimeMinutes,
      timeUntilEventMinutes: timeUntilEventMinutes,
      eventName: calendarEvent.summary || 'Untitled Event',
      eventLocation: calendarEvent.location || 'No location specified',
      eventStartTime: eventStartTime,
      currentTime: currentTime.toISOString()
    }
  }
];

