// TIME PLANNING - Cold Email Sequence
// Objectif : J0, J+3, J+5/7 (avec contraintes jours & random)
// YOUR_AWS_SECRET_KEY_HERE======
const items = $input.all();

// Configuration de base
const OFFSETS = [0, 3, 6];  // J0, J+3, J+6 (‚âà J5‚Äì7 selon les jours autoris√©s)
const MIN_GAP_DAYS = 2;     // au moins 2 jours entre chaque email
const SEND_HOUR = 9;
const SEND_MIN = 0;
const RANDOMIZE = true;
const MAX_RANDOM_MIN = 120; // 0‚Äì120 min ‚Üí 9h‚Äì11h

const NOW = new Date();

// Jours autoris√©s : mardi (2), mercredi (3), jeudi (4)
function isAllowedDay(date) {
  const day = date.getDay();
  return day === 2 || day === 3 || day === 4;
}

function moveToNextAllowedDay(date) {
  let d = new Date(date);
  let attempts = 0;
  while (!isAllowedDay(d) && attempts < 10) {
    d.setDate(d.getDate() + 1);
    attempts++;
  }
  if (attempts >= 10) throw new Error('No allowed day found');
  return d;
}

function addDays(date, days) {
  const d = new Date(date);
  d.setDate(d.getDate() + days);
  return d;
}

function setTime(date, hour, min) {
  const d = new Date(date);
  d.setHours(hour, min, 0, 0);
  return d;
}

// pseudo-random stable par prospect + index d'email
function getRandomOffset(seed, max) {
  if (!RANDOMIZE) return 0;
  let hash = 0;
  for (let i = 0; i < seed.length; i++) {
    hash = ((hash << 5) - hash) + seed.charCodeAt(i);
    hash = hash & hash;
  }
  return Math.abs(hash) % max;
}

function diffInDays(a, b) {
  const msPerDay = 24 * 60 * 60 * 1000;
  return Math.floor((a - b) / msPerDay);
}

return items.map(item => {
  const json = item.json;
  const createdAt = json.created_at || NOW.toISOString();
  const created = new Date(createdAt);

  const baseSeed = json.Email || String(json.id || NOW.getTime());
  const result = { ...json };

  let prevSend = null;

  OFFSETS.forEach((offsetDays, index) => {
    // 1) base J+offset
    let target = addDays(created, offsetDays);

    // 2) si on a d√©j√† envoy√© un email, on impose un gap minimum
    if (prevSend) {
      const minNextDate = addDays(prevSend, MIN_GAP_DAYS);
      if (target < minNextDate) {
        target = minNextDate;
      }
    }

    // 3) respecter les jours autoris√©s (mar/mer/jeu)
    target = moveToNextAllowedDay(target);

    // 4) heure de base 9h00
    target = setTime(target, SEND_HOUR, SEND_MIN);

    // 5) randomiser l'heure pour cet email
    const randomMin = getRandomOffset(baseSeed + '_' + (index + 1), MAX_RANDOM_MIN);
    if (RANDOMIZE) {
      target.setMinutes(target.getMinutes() + randomMin);
    }

    // 6) s'assurer que la date est dans le futur
    if (target <= NOW) {
      let future = addDays(NOW, 1);
      future = moveToNextAllowedDay(future);
      future = setTime(future, SEND_HOUR, SEND_MIN);
      const futureRandom = getRandomOffset(baseSeed + '_future_' + (index + 1), MAX_RANDOM_MIN);
      if (RANDOMIZE) {
        future.setMinutes(future.getMinutes() + futureRandom);
      }
      target = future;
    }

    result[`email${index + 1}_at`] = target.toISOString();
    prevSend = target;
  });

  return { json: result };
});

