parameters.functionCode:
// Prepare ETL logs
const data = $json.body;
const logContent = data.log || "";
const maxLength = 2000;
const logPreview = logContent.substring(0, maxLength);
const fullLogLink = "https://s3.amazonaws.com/full-log/" + data.job_id + ".log";
return [{ json: { job_id: data.job_id, pipeline_name: data.pipeline_name, error_message: data.error_message, logPreview, fullLogLink } }];

parameters.jsCode:
const raw = $json.output;

try {
  const parsed = JSON.parse(raw);
  return [{ json: parsed }];
} catch (e) {
  return [{ json: { error: true, message: "Invalid JSON returned", raw } }];
}

