// Get all original data from the 'Filter Duplicates' node
const originalItems = $('Filter Duplicates').all();

// Get all results from the 'AI Agent' node
const aiItems = $input.all();

// Combine data in order and return
return aiItems.map((item, index) => {
  const aiText = item.json.output || item.json.text || "";

  // Split the AI text into "Summary" and "Reply Draft" based on the English markers
  const summaryMatch = aiText.match(/\[Summary\]\s*([\s\S]*?)\s*(?=\[Reply Draft\]|$)/);
  const replyMatch = aiText.match(/\[Reply Draft\]\s*([\s\S]*)/);

  const summary = summaryMatch ? summaryMatch[1].trim() : "";
  const reply = replyMatch ? replyMatch[1].trim() : "";

  // Retrieve original data based on index
  // Assuming AI processing order matches the original data order
  const originalData = originalItems[index] ? originalItems[index].json : {};

  return {
    json: {
      ...originalData,      // Inherit reviewId, stars, text, etc.
      ai_summary: summary,  // Split summary
      ai_reply: reply,      // Split reply draft
      output: aiText        // Full AI output
    }
  };
});
