// Function is set to "Run Once for All Items"

const rawHtml = $input.first().json.results[0].content; // Assuming this is the raw HTML output

// 1. Aggressively strip known boilerplate tags and content (CSS, Scripts, HTML headers)
let textContent = rawHtml
    .replace(/<script\b[^>]*>([\s\S]*?)<\/script>/gi, '') // Remove script blocks
    .replace(/<style\b[^>]*>([\s\S]*?)<\/style>/gi, '')   // Remove style blocks
    .replace(/<head\b[^>]*>([\s\S]*?)<\/head>/gi, '')     // Remove header blocks
    .replace(/<\/?[^>]+(>|$)/g, "")                       // Remove all remaining HTML tags
    .toUpperCase(); // Convert to uppercase for case-insensitive keyword searching

// 2. Define Universal Slicing Markers (Based on commercial page structure)
const startMarkers = ['PRODUCTS', 'FEATURES', 'PRICING', 'FREE FOREVER'];
const endMarkers = ['FREQUENTLY ASKED QUESTIONS', 'FAQ', 'LEGAL', 'TERMS OF SERVICE', 'ABOUT US', 'COMPANY'];

let effectiveStart = 0;
let effectiveEnd = textContent.length;

// 3. Find START (First Product/Pricing Section)
for (const marker of startMarkers) {
    let index = textContent.indexOf(marker);
    if (index !== -1) {
        effectiveStart = index;
        break;
    }
}

// 4. Find END (Last Footer/Legal Section)
for (const marker of endMarkers) {
    let index = textContent.indexOf(marker);
    if (index !== -1 && index > effectiveStart + 100) { // Add buffer
        effectiveEnd = index;
        break;
    }
}

const isolatedPricingSection = textContent.slice(effectiveStart, effectiveEnd).trim();

// 5. Output the super-clean text
return [{
    json: {
        clean_pricing_text: isolatedPricingSection,
        status: isolatedPricingSection.length > 500 ? 'SUCCESS' : 'FAILURE: Content Too Short'
    }
}];

// This Code Node must be set to run ONCE for ALL ITEMS,
// as the input is typically the full array of current plans.

// 1. Get the current and historical data from the merged input item:
const currentPlans = $input.first().json.output || [];
const historicalRow = $('Loop Monitor Each Plan').first(); // Data from Sheets Read node

const lastPlansJson = historicalRow.json['Last Plans'] || '[]';

const alertItems = []; // Array to hold ONLY the plans requiring an alert

try {
    // 2. Parse the historical JSON string into a usable array of objects
    const lastPlans = JSON.parse(lastPlansJson); 

    const diffs = [];

    // Function to calculate the difference for a single plan
    const getPlanDiff = (currentPlan, lastPlans) => {

    // Find the historical record that matches the current plan name and billing cycle
    const oldPlan = lastPlans.find(p => 
      p.plan_name === currentPlan.plan_name && 
      p.billing_cycle === currentPlan.billing_cycle
    );

    // Ensure prices are numeric (already done in the previous Code node, but we'll re-parse the old plan string for safety)
        const currentPriceString = currentPlan.price_usd;

        const oldPriceString = oldPlan ? oldPlan.price_usd : null;

        // Clean the old price string (remove $, text) and convert to number


        const oldPrice = oldPriceString.toUpperCase().includes('FREE')
          ? 0 
          : parseFloat(oldPriceString.replace(/[^0-9.]/g, ''));


        const currentPrice = currentPriceString.toUpperCase().includes('FREE')
          ? 0 
          : parseFloat(currentPriceString.replace(/[^0-9.]/g, ''));


        let priceDiff = null;
        let priceStatus='';

      if (currentPrice !== null && oldPrice !== null) {

          if (oldPrice === 0 && currentPrice > 0) {
              // Case 1: FREE to PAID (Division by Zero Scenario)
              priceDiff = 9999; // Assign a high value to force an alert
              priceStatus = 'FREE_TO_PAID_ALERT';
          } else if (oldPrice > 0) {
              // Case 2: Standard Paid-to-Paid Calculation
              priceDiff = ((currentPrice - oldPrice) / oldPrice) * 100;
              priceStatus = priceDiff > 0 ? 'PRICE_INCREASE' : 'PRICE_DECREASE';
          } else if (oldPrice > 0 && currentPrice === 0) {
              // Case 3: Paid-to-FREE (oldPrice > 0 and currentPrice === 0)
              priceDiff = -100;
              priceStatus = 'PAID_TO_FREE_ALERT';
          }
      }

        return {
            ...currentPlan, // Keep all current plan details
            old_price_numeric: oldPrice,
            current_price_numeric: currentPrice,
            price_change_percent: priceDiff,
            price_status: priceStatus,
            alert_status: (priceDiff !== null && Math.abs(priceDiff) >= $('Config: Alert Parameters').first().json.alert_threshold_percent) 
                ? 'ALERT' 
                : 'NO_CHANGE',
        };
    };

    // 3. Iterate and calculate diffs for all plans
    for (const plan of currentPlans) {
        const diffPlan = getPlanDiff(plan, lastPlans);
        diffs.push(diffPlan);

          // 2. CRUCIAL FILTER: Only push items that require immediate action
        if (diffPlan.alert_status === 'ALERT') { 
            alertItems.push(diffPlan);
        }
    }

    return [{ json: { price_diffs: diffs,
                    alert_items: alertItems} }];

} catch (e) {
    // Handle JSON parsing error if the old 'Last Plans' column was corrupted
    return [{ json: { error: "JSON_PARSE_ERROR", message: e.message } }];
}
