// Starting from the wrapped original workflow
const original = $json.originalWorkflowJson;

// Recursively remove heavy fields that we *know* we don't need the AI for.
function walk(value, depth = 0) {
  if (Array.isArray(value)) {
    return value.map(v => walk(v, depth + 1));
  }
  if (value && typeof value === 'object') {
    const out = {};
    for (const [key, val] of Object.entries(value)) {
      // Remove credentials everywhere
      if (key === 'credentials') continue;
      // Remove webhookId everywhere
      if (key === 'webhookId') continue;
      // Remove some top-level metadata
      if (depth === 0 && (key === 'meta' || key === 'versionId' || key === 'id')) continue;

      out[key] = walk(val, depth + 1);
    }
    return out;
  }
  return value;
}

const preTrimmed = walk(original);

return [{
  json: {
    originalWorkflowJson: original,
    preTrimmedWorkflowJson: preTrimmed,
  },
}];

// 1) Get the text returned by the OpenAI node (Message a model ‚Äì Sanitize)
let raw = $json.output?.[0]?.content?.[0]?.text;

if (typeof raw !== 'string') {
  raw = JSON.stringify(raw ?? {});
}

// 2) Parse sanitized workflow JSON
const sanitized = JSON.parse(raw);

// 3) Also create a pretty JSON string for file export
const sanitizedText = JSON.stringify(sanitized, null, 2);

// 4) Return both
return [{
  json: {
    sanitizedWorkflowJson: sanitized,
    sanitizedText
  }
}];
