const items = $input.all();
const brand = $('Brand Config').first().json.brand

const normalized = [];

items.forEach(item => {
  const hits = item.json.hits || [];

  hits.forEach(hit => {
    normalized.push({
      platform: 'Hacker News',
      title: hit.title || '',
      url: hit.url || `https://news.ycombinator.com/item?id=${hit.objectID}`,
      snippet: hit.story_text || hit.title || '',
      author: hit.author || 'unknown',
      created_at: hit.created_at || new Date(hit.created_at_i * 1000).toISOString(),
      brand: brand,
      points: hit.points || 0,
      num_comments: hit.num_comments || 0
    });
  });
});

return normalized.map(item => ({ json: item }));

const items = $input.all();
const brand = items[0]?.json.brand || 'VivekLabs';

const totalMentions = items.length;
const sentimentCounts = {
  positive: 0,
  negative: 0,
  neutral: 0
};

items.forEach(item => {
  const sentiment = item.json.sentiment || 'neutral';
  if (sentimentCounts[sentiment] !== undefined) {
    sentimentCounts[sentiment]++;
  }
});

const top10 = items.slice(0, 10);

let reportText = `*Daily Brand Mentions Report for ${brand}*\n\n`;
reportText += `üìä *Total Mentions:* ${totalMentions}\n`;
reportText += `‚úÖ Positive: ${sentimentCounts.positive}\n`;
reportText += `‚ùå Negative: ${sentimentCounts.negative}\n`;
reportText += `‚ö™ Neutral: ${sentimentCounts.neutral}\n\n`;
reportText += `*Top Mentions:*\n`;

top10.forEach((item, index) => {
  const data = item.json;
  const emoji = data.sentiment === 'positive' ? '‚úÖ' : data.sentiment === 'negative' ? '‚ùå' : '‚ö™';
  const title = data.title || 'No title';
  const platform = data.platform || 'Hackers Info';
  const topic = data.topic || 'other';
  const urgency = data.urgency || 'low';
  const url = data.url || '';

  reportText += `${index + 1}. ${emoji} *${title}*\n`;
  reportText += `   Platform: ${platform} | Topic: ${topic} | Urgency: ${urgency}\n`;
  reportText += `   ${url}\n\n`;
});

return [{ json: { text: reportText } }];

