parameters.jsCode:
const items = [];

for (const item of $input.all()) {
  const output = item.json.output;

  // Extract the record ID from the input item
  // Assuming the record ID comes from a previous Airtable node
  const recordId = $('Split Into Batches').first().json.id || "Unknown" ;

  // Convert bullet features array to newline-separated string
  const bulletFeaturesText = output.ai_bullet_features.join('\n');

  // Convert feature table to JSON string
  const featureTableJson = JSON.stringify(output.ai_feature_table);

  items.push({
    json: {
      id: recordId,
      ai_long_description: output.ai_long_description,
      ai_short_answer_block: output.ai_short_answer_block,
      ai_bullet_features: bulletFeaturesText,
      ai_feature_table_json: featureTableJson,
      ai_status: 'done',
      ai_last_run_at: $now.toISO()
    },
    pairedItem: item.pairedItem
  });
}

return items;

