parameters.jsonBody:
={
  "url_to_scrape": "https://www.google.com/maps/search/{{ $json.City }}+{{ $json.business }}",
  "wait_before_scraping": 100000,
  "formats": [
    "json"
  ],
  "remove_css_selectors": "default",
  "actions": [
      {
        "type": "wait",
        "milliseconds": 10000
    },
    {
        "type": "click",
        "selector": ".PLbyfe"
    },
    {
        "type": "scroll",
        "direction": "down",
        "amount": 100
    },
    {
        "type": "wait",
        "milliseconds": 10000
    },
    {
        "type": "scroll",
        "direction": "down",
        "amount": 1000
    }
  ],
  "country": "US",
  "transformer": "none",
  "remove_images": false,
  "remove_class_names": [],
  "parser": {},
  "llm_extract": {
    "schema": {
      "type": "array",
      "description": "A list of businesses found on the Google Maps search results page.",
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "The name of the business or place."
          },
          "website": {
            "type": "string",
            "description": "The official website URL for the business."
          },
          "location": {
            "type": "string",
            "description": "A short snippet of the business address or location."
          },
          "phone_number": {
            "type": "string",
            "description": "The phone number of the business or place."
          }
        },
        "required": [
          "name",
          "website_url"
        ]
      }
    }
  },
  "links_on_page": {
    "absolute_links": true,
    "query_to_order_links_by": "",
    "include_links": [],
    "exclude_links": []
  },
  "screen_size": {
    "screen_type": "desktop",
    "screen_width": 1920,
    "screen_height": 1080
  },
  "screenshot": {
    "full_page": true
  },
  "metadata": {}
}

={
  "url_to_scrape": "{{ $json.website}}",
  "wait_before_scraping": 10000,
  "formats": [
    "json"
  ],
  "remove_css_selectors": "default",
  "actions": [],
  "country": "US",
  "transformer": "none",
  "remove_images": false,
  "remove_class_names": [],
  "parser": {},
  "llm_extract": {
    "schema": {
      "type": "object",
      "properties": {
        "firstName": {
          "type": "string",
          "description": "The first name of the CEO or the decision maker in the business."
        },
        "lastName": {
          "type": "string",
          "description": "The last name of the CEO or the decision maker in the business."
        },
        "email": {
          "type": "string",
          "description": "The email of the business."
        }
      },
      "required": [
        "firstName",
        "lastName"
      ]
    }
  },
  "links_on_page": {
    "absolute_links": true,
    "query_to_order_links_by": "",
    "include_links": [],
    "exclude_links": []
  },
  "screen_size": {
    "screen_type": "default",
    "screen_width": 123,
    "screen_height": 123
  },
  "screenshot": {
    "full_page": true
  },
  "metadata": {}
}
